// src/App.js - The main App component with navigation

import React, { useState, useEffect } from 'react';
import ResumeBuilder from './Resumebuilder'; // Import the new component we created
import './index.css';

// This is a simple component just for displaying the opportunities list.
// We've moved it inside App.js to keep things organized.
function OpportunitiesView() {
  const [opportunities, setOpportunities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('/api/opportunities')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok. Is the server running?');
        }
        return response.json();
      })
      .then(data => {
        setOpportunities(data);
        setLoading(false);
      })
      .catch(error => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading opportunities...</div>;
  if (error) return <div className="error-message">Error: {error}</div>;

  return (
    <div>
      <h2>Latest Opportunities</h2>
      <div className="opportunities-list">
        {opportunities.map(op => {
          const typeClass = op.type.split(' ')[0].toLowerCase();
          const cardClassName = `opportunity-card card-${typeClass}`;
          return (
            <div className={cardClassName} key={op.id}>
              <h3>{op.title} <span className="opportunity-type">({op.type})</span></h3>
              <p>{op.description}</p>
              <a href={op.url} target="_blank" rel="noopener noreferrer">Learn More</a>
            </div>
          );
        })}
      </div>
    </div>
  );
}


// This is the main App component that controls which view is shown
function App() {
  // We use state to remember which page the user is on. 'opportunities' is the default.
  const [view, setView] = useState('opportunities'); 

  return (
    <div className="App">
      <header>
        <h1>PathFinder</h1>
        <nav className="nav">
          <button 
            className={`nav-button ${view === 'opportunities' ? 'active' : ''}`} 
            onClick={() => setView('opportunities')}
          >
            Opportunities
          </button>
          <button 
            className={`nav-button ${view === 'builder' ? 'active' : ''}`} 
            onClick={() => setView('builder')}
          >
            AI Resume Builder
          </button>
        </nav>
      </header>

      <main>
        {/* This is called conditional rendering. It shows the correct component based on the 'view' state. */}
        {view === 'opportunities' && <OpportunitiesView />}
        {view === 'builder' && <ResumeBuilder />}
      </main>
    </div>
  );
}

export default App;
