// src/ResumeBuilder.js - AI Resume Builder Component (File Upload Focused)

import React, { useState } from 'react';

function ResumeBuilder() {
  const [formData, setFormData] = useState({
    dreamJob: '',
    experienceLevel: 'Student / Entry-Level',
  });

  // We'll use this to track the uploaded file
  const [resumeFile, setResumeFile] = useState(null); 
  const [fileName, setFileName] = useState('');

  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setResumeFile(file);
      setFileName(file.name);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!resumeFile) {
      alert("Please upload a resume file to continue.");
      return;
    }

    setIsLoading(true);
    setResult(null);
    setError(null);

    // --- PROTOTYPE NOTE ---
    // In a real app, you would send the `resumeFile` to the server to be parsed.
    // For this prototype, we will send some placeholder text to the API 
    // to demonstrate the analysis feature.
    const placeholderResumeText = `Experience working on several projects. Responsible for design and development. Skills include JavaScript and communication.`;

    const userPreferences = {
      dreamJob: formData.dreamJob,
      experienceLevel: formData.experienceLevel,
    };

    fetch('http://localhost:3001/api/resume/analyze', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        resumeText: placeholderResumeText, // Using placeholder text for now
        userPreferences: userPreferences,
      }),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Analysis failed. Please check the server.');
        }
        return response.json();
      })
      .then(data => {
        setResult(data);
      })
      .catch(err => {
        setError(err.message);
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  return (
    <div>
      <div className="hero-section">
        <h2>AI-Powered Resume & Career Blueprint</h2>
        <p>Upload your current resume, fill out your details, and let our AI craft the perfect resume and suggest the next steps for your career.</p>
      </div>

      <form onSubmit={handleSubmit} className="resume-builder-form">
        <div className="form-grid">
          <div className="form-group">
            <label htmlFor="dreamJob">What is your dream job?</label>
            <input
              type="text"
              id="dreamJob"
              name="dreamJob"
              value={formData.dreamJob}
              onChange={handleInputChange}
              placeholder="e.g., Software Engineer"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="experienceLevel">What is your experience level?</label>
            <select
              id="experienceLevel"
              name="experienceLevel"
              value={formData.experienceLevel}
              onChange={handleInputChange}
            >
              <option>Student / Entry-Level</option>
              <option>Mid-Level Professional (2-5 years)</option>
              <option>Senior Professional (5+ years)</option>
            </select>
          </div>
        </div>

        <div className="upload-section">
          <label htmlFor="resume-file-upload" className="upload-label">
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path><polyline points="17 8 12 3 7 8"></polyline><line x1="12" y1="3" x2="12" y2="15"></line></svg>
            <p><span>Upload your resume</span> or drag and drop</p>
            <span style={{fontSize: '0.8rem', color: '#6B7280'}}>PDF, DOC, DOCX, TXT</span>
          </label>
          <input id="resume-file-upload" type="file" onChange={handleFileChange} accept=".pdf,.doc,.docx,.txt" />
          {fileName && <p className="file-name">Selected: {fileName}</p>}
        </div>

        <button type="submit" className="submit-button" disabled={isLoading || !fileName}>
          {isLoading ? 'Analyzing...' : 'Generate My Blueprint'}
        </button>
      </form>

      {/* Results Section */}
      {error && <p className="error-message">Error: {error}</p>}
      {result && (
        <div className="results-section" style={{marginTop: '3rem', background: 'white', padding: '2rem', borderRadius: '12px', boxShadow: 'var(--card-shadow)'}}>
          <h2>Your Career Blueprint</h2>
          {/* We will build out a more detailed result view here later */}
          <pre>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default ResumeBuilder;
