import React from 'react';
import { ArrowRight, Clock } from 'lucide-react';
import { TrophyIcon, FileTextIcon, TargetIcon, UsersIcon } from './Iconcomponents';

// Data for the feature cards
const categories = [
  {
    id: "competitions",
    title: "Competition Aggregator",
    description: "Join exciting challenges and showcase your skills",
    icon: TrophyIcon,
    neonColor: "neon-blue",
  },
  {
    id: "resume",
    title: "Resume Builder (The Filter)",
    description: "AI-powered tools to create the perfect resume",
    icon: FileTextIcon,
    neonColor: "neon-purple",
  },
  {
    id: "practice",
    title: "Practice Sessions (The Guide)",
    description: "Skill-building sessions with expert guidance",
    icon: TargetIcon,
    neonColor: "neon-green",
  },
  {
    id: "community",
    title: "Community Platform",
    description: "Connect, collaborate, and grow with peers",
    icon: UsersIcon,
    neonColor: "neon-pink",
  }
];

export function FeatureCategories() {
  return (
    <section className="py-20 bg-black relative overflow-hidden">
      <div className="absolute inset-0 cyber-grid opacity-20"></div>
      <div className="container mx-auto px-4 relative z-10">
        <div className="text-center mb-16">
          <h2 className="font-orbitron text-4xl md:text-5xl font-bold mb-6 text-white neon-text">
            Explore Our <span className="bg-gradient-to-r from-neon-blue via-neon-purple to-neon-pink bg-clip-text text-transparent">
              Cyber Features
            </span>
          </h2>
          <p className="text-xl text-white/80 max-w-3xl mx-auto leading-relaxed">
            From competitions to skill building, discover all the digital tools you need to accelerate your career journey.
          </p>
        </div>
        <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
          {categories.map((category) => {
            const IconComponent = category.icon;
            return (
              <div key={category.id} className={`group hover:shadow-2xl transition-all duration-500 border-0 shadow-xl hologram hover:scale-105 hover:-translate-y-2 rounded-2xl overflow-hidden hover:shadow-${category.neonColor}`}>
                <div className={`h-2 bg-gradient-to-r from-${category.neonColor} to-transparent`}></div>
                <div className="p-8">
                  <div className={`p-4 inline-block rounded-2xl bg-${category.neonColor}/20 border border-${category.neonColor}/30 shadow-lg transform group-hover:scale-110 transition-all duration-300 mb-6`}>
                    <IconComponent className="h-8 w-8" />
                  </div>
                  <h3 className="font-orbitron text-xl font-bold text-white mb-3">{category.title}</h3>
                  <p className="text-white/80 leading-relaxed mb-6">{category.description}</p>
                  <button className={`w-full bg-gradient-to-r from-${category.neonColor} to-${category.neonColor}/80 hover:shadow-xl text-black border-0 rounded-xl py-3 font-semibold transform hover:scale-105 transition-all duration-300 neon-text`}>
                    Explore Feature <ArrowRight className="inline ml-2 h-4 w-4" />
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
}
