// src/ResumeBuilder.js - AI Resume Builder Component with File Upload

import React, { useState } from 'react';

function ResumeBuilder() {
  const [formData, setFormData] = useState({
    dreamJob: '',
    experienceLevel: 'Student / Entry-Level',
    preference: 'Any',
    resumeText: ''
  });
  const [fileName, setFileName] = useState('');
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFileName(file.name);
      // In a real app, you would read the file content here.
      // For this prototype, we'll just show an alert.
      alert(`File "${file.name}" selected. For this demo, please still paste your resume text to enable analysis.`);
      // Note: To parse .pdf or .docx, you'd need a library like Tika, pdf-parse (on server), or mammoth.js
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsLoading(true);
    setResult(null);
    setError(null);

    const userPreferences = {
      dreamJob: formData.dreamJob,
      experienceLevel: formData.experienceLevel,
      preference: formData.preference,
    };

    fetch('http://localhost:3001/api/resume/analyze', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        resumeText: formData.resumeText,
        userPreferences: userPreferences,
      }),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Analysis failed. Please check the server.');
        }
        return response.json();
      })
      .then(data => {
        setResult(data);
      })
      .catch(err => {
        setError(err.message);
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  return (
    <div>
      <div className="hero-section">
        <h2>AI-Powered Resume & Career Blueprint</h2>
        <p>Upload or paste your current resume, fill out your details, and let our AI craft the perfect resume and suggest the next steps for your career.</p>
      </div>

      <form onSubmit={handleSubmit} className="resume-builder-form">
        <div className="upload-section">
          <label htmlFor="resume-file-upload" className="upload-label">
            <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path><polyline points="17 8 12 3 7 8"></polyline><line x1="12" y1="3" x2="12" y2="15"></line></svg>
            <p><span>Upload a file</span> or drag and drop</p>
          </label>
          <input id="resume-file-upload" type="file" onChange={handleFileChange} accept=".pdf,.doc,.docx,.txt" />
          {fileName && <p className="file-name">Selected: {fileName}</p>}
        </div>

        <div className="separator">OR</div>

        <div className="form-grid">
          <div className="form-group">
            <label htmlFor="dreamJob">What is your dream job?</label>
            <input
              type="text"
              id="dreamJob"
              name="dreamJob"
              value={formData.dreamJob}
              onChange={handleInputChange}
              placeholder="e.g., Software Engineer"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="experienceLevel">What is your experience level?</label>
            <select
              id="experienceLevel"
              name="experienceLevel"
              value={formData.experienceLevel}
              onChange={handleInputChange}
            >
              <option>Student / Entry-Level</option>
              <option>Mid-Level Professional (2-5 years)</option>
              <option>Senior Professional (5+ years)</option>
            </select>
          </div>
        </div>

        <div className="form-group full-width">
          <label htmlFor="resumeText">Paste your current resume text here:</label>
          <textarea
            id="resumeText"
            name="resumeText"
            value={formData.resumeText}
            onChange={handleInputChange}
            placeholder="e.g., John Doe - Responsible for making a website..."
            required
          ></textarea>
        </div>

        <button type="submit" className="submit-button" disabled={isLoading}>
          {isLoading ? 'Analyzing...' : 'Generate My Blueprint'}
        </button>
      </form>

      {/* Results Section */}
      {error && <p className="error-message">Error: {error}</p>}
      {result && (
        <div className="results-section">
          <h2>Your Career Blueprint</h2>
          {/* We will build out a more detailed result view here later */}
          <pre>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default ResumeBuilder;
