// src/pages/AIFriend.js
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import { Send, MessageCircle, Zap } from "lucide-react";

const mentors = [
  { type: "Tech Mentor", name: "Fazal", color: "#4ade80", description: "Expert in software development, AI, and cloud technologies." },
  { type: "Design Mentor", name: "Alaana", color: "#f472b6", description: "Creative designer specializing in UX/UI and digital art." },
  { type: "Career Coach", name: "Khaliq and Shafiq", color: "#60a5fa", description: "Helping professionals identify strengths and plan their careers." },
  { type: "Entrepreneur Guide", name: "Omar", color: "#facc15", description: "Experienced startup founder and business strategist." },
];

const AIFriend = () => {
  const [selectedMentor, setSelectedMentor] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim() || !selectedMentor) return;

    const newMsg = { role: "user", content: input, time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }) };
    setMessages((prev) => [...prev, newMsg]);
    setInput("");

    try {
      const res = await axios.post("http://localhost:3001/api/mentor/chat", {
        userId: "user123",
        mentorType: selectedMentor.type,
        message: input,
      });
      setMessages(res.data.messages);
    } catch (err) {
      console.error("Error sending message:", err);
    }
  };

  return (
    <div className="min-h-screen bg-black flex flex-col md:flex-row text-white p-8 md:space-x-8 font-sans">
      {/* Left Panel */}
      <motion.div
        initial={{ x: -20, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.6 }}
        className="w-full md:w-1/3 bg-black/50 border border-gray-800 rounded-2xl p-6 backdrop-blur-xl shadow-lg flex flex-col items-center text-center"
      >
        <div className="flex flex-col items-center mb-4">
          <div
            className="w-20 h-20 rounded-full flex items-center justify-center text-3xl font-bold text-black"
            style={{
              background: selectedMentor ? selectedMentor.color : "#60a5fa",
              boxShadow: `0 0 20px ${selectedMentor ? selectedMentor.color : "#60a5fa"}70`,
            }}
          >
            {selectedMentor ? selectedMentor.name.charAt(0) : "A"}
          </div>
          <h2 className="mt-4 text-2xl font-semibold">{selectedMentor ? selectedMentor.name : "Your AI Friend"}</h2>
          <p className="text-gray-400 text-sm">{selectedMentor ? selectedMentor.type : "Choose a mentor to begin"}</p>
        </div>

        {selectedMentor ? (
          <>
            <p className="text-gray-300 text-sm mb-6">{selectedMentor.description}</p>
            <button onClick={() => setMessages([])} className="w-full border border-gray-700 text-gray-300 rounded-xl py-2 mb-3 hover:bg-gray-800 transition">
              ðŸ’¬ New Chat
            </button>
            <button onClick={() => setSelectedMentor(null)} className="w-full border border-gray-700 text-gray-300 rounded-xl py-2 hover:bg-gray-800 transition">
              ðŸ”„ Change Mentor
            </button>
          </>
        ) : (
          <div className="w-full space-y-3">
            <p className="text-gray-400 text-sm">Select a mentor to start chatting:</p>
            {mentors.map((m) => (
              <button
                key={m.name}
                onClick={() => setSelectedMentor(m)}
                className="w-full border border-gray-700 text-gray-300 rounded-xl py-3 hover:bg-gray-800 transition text-sm"
              >
                {m.name} â€” {m.type}
              </button>
            ))}
          </div>
        )}
      </motion.div>

      {/* Right Panel with Chat + Aura Section */}
      <motion.div
        initial={{ x: 20, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.6 }}
        className="w-full md:w-2/3 flex flex-col bg-[#0d0d0f] border border-gray-800 rounded-2xl shadow-2xl relative overflow-hidden"
      >
        {/* --- AURA HEADER + Quick Actions --- */}
        <div className="border-b border-gray-800 p-5 flex flex-col space-y-4 bg-gradient-to-b from-[#0f1115] to-[#0d0d0f]">
          <div className="flex justify-between items-center">
            <div>
              <h2 className="text-2xl font-semibold tracking-wide">AURA</h2>
              <p className="text-gray-400 text-sm">Career Coach</p>
            </div>
            <button className="text-gray-400 text-sm hover:text-gray-200 transition">Close Chat</button>
          </div>

          {/* Quick Actions */}
          <div>
            <h3 className="text-gray-300 text-sm mb-2">âš¡ Quick Actions</h3>
            <div className="flex flex-wrap gap-3">
              {["Create Resume", "Schedule Mock Interview", "Find Internships", "Review Applications"].map((action) => (
                <button
                  key={action}
                  className="flex items-center gap-2 px-4 py-2 text-sm rounded-xl bg-[#16181c] border border-gray-700 hover:bg-[#1f2127] transition"
                >
                  <Zap size={14} /> {action}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* --- Chat Messages --- */}
        <div className="flex-grow overflow-y-auto px-6 py-6 space-y-6">
          {selectedMentor ? (
            messages.length > 0 ? (
              messages.map((msg, i) => (
                <motion.div
                  key={i}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3 }}
                  className={`flex ${msg.role === "user" ? "justify-end" : "justify-start"}`}
                >
                  <div
                    className={`relative max-w-[70%] px-4 py-3 rounded-2xl text-sm leading-relaxed ${
                      msg.role === "user"
                        ? "bg-[#2563eb] text-white"
                        : "bg-[#1e1e22] text-gray-100 border border-gray-800"
                    }`}
                  >
                    {msg.content}
                    <span className="block text-[10px] text-gray-400 mt-1 text-right">
                      {msg.time || "09:59 PM"}
                    </span>
                  </div>
                </motion.div>
              ))
            ) : (
              <div className="text-gray-500 text-center mt-20 text-sm">
                <MessageCircle className="mx-auto mb-2 text-gray-600" />
                Say hi to {selectedMentor.name} to begin your chat!
              </div>
            )
          ) : (
            <div className="text-gray-500 text-center mt-20 text-sm">
              <MessageCircle className="mx-auto mb-2 text-gray-600" />
              Select a mentor from the left to start chatting!
            </div>
          )}
          <div ref={chatEndRef} />
        </div>

        {/* --- Chat Input --- */}
        {selectedMentor && (
          <form onSubmit={sendMessage} className="flex items-center gap-3 border-t border-gray-800 bg-[#0d0d0f]/90 px-4 py-3">
            <input
              type="text"
              placeholder={`Ask ${selectedMentor.name} anything...`}
              className="flex-grow bg-[#1a1a1d] border border-gray-700 rounded-xl text-gray-200 px-4 py-3 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 placeholder-gray-500"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
            <button type="submit" className="bg-[#2563eb] hover:bg-[#1d4ed8] text-white p-3 rounded-xl transition-all">
              <Send className="w-5 h-5" />
            </button>
          </form>
        )}
      </motion.div>
    </div>
  );
};

export default AIFriend;
