import React, { useState } from 'react';
import BlueprintResults from './Blueprintresults'; // Assuming this component exists to show results

// A simple SVG icon for the upload area
const UploadIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" className="mx-auto mb-4 text-cyan-400/50 group-hover:text-neon-blue transition-colors">
        <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4" />
        <polyline points="17 8 12 3 7 8" />
        <line x1="12" y1="3" x2="12" y2="15" />
    </svg>
);

function ResumeBuilder() {
    const [userPreferences, setUserPreferences] = useState({
        dreamJob: '',
        experienceLevel: 'Student / Entry-Level',
        preference: 'any',
    });
    const [resumeFile, setResumeFile] = useState(null);
    const [resumeText, setResumeText] = useState("Responsible for making a website for a school project. I used javascript. Also I worked at a cafe. Skills: javascript, talking to people."); // Default text for prototype
    const [analysisResult, setAnalysisResult] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setResumeFile(file);
            // In a real app, you'd process the file here. For now, we just note the name.
            console.log("File selected:", file.name);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserPreferences(prev => ({ ...prev, [name]: value }));
    };

    const handleGenerate = async (e) => {
        e.preventDefault();
        if (!resumeFile) {
            setError('Please upload a resume file to begin.');
            return;
        }
        setIsLoading(true);
        setError('');
        setAnalysisResult(null);

        // This is where you would normally read the resume file content.
        // For this prototype, we'll continue to use the default 'resumeText'.

        try {
            const response = await fetch('http://localhost:3001/api/resume/analyze', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ resumeText, userPreferences }),
            });

            if (!response.ok) {
                throw new Error('Analysis failed. Please check the server.');
            }

            const data = await response.json();
            setAnalysisResult(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    if (analysisResult) {
        return <BlueprintResults data={analysisResult} onBack={() => setAnalysisResult(null)} />;
    }

    return (
        <div className="p-4 md:p-8 text-white">
            <div className="max-w-4xl mx-auto">
                <div className="text-center mb-12">
                    <h2 className="font-orbitron text-3xl md:text-5xl font-bold mb-4 neon-text">AI-POWERED BLUEPRINT</h2>
                    <p className="text-lg text-white/70">Upload your resume, define your goals, and let our AI craft the perfect career path for you.</p>
                </div>

                <form onSubmit={handleGenerate} className="bg-black/40 backdrop-blur-md border border-cyan-400/30 rounded-2xl p-8 shadow-lg">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                        {/* Dream Job Input */}
                        <div>
                            <label htmlFor="dreamJob" className="block font-semibold mb-2">What is your dream job?</label>
                            <input
                                type="text"
                                id="dreamJob"
                                name="dreamJob"
                                value={userPreferences.dreamJob}
                                onChange={handleInputChange}
                                className="w-full bg-white/5 border border-cyan-400/30 rounded-lg px-4 py-3 text-white placeholder-white/60 focus:ring-2 focus:ring-neon-blue focus:border-neon-blue outline-none transition-all"
                                placeholder="e.g., Software Engineer"
                                required
                            />
                        </div>
                        {/* Experience Level Select */}
                        <div>
                            <label htmlFor="experienceLevel" className="block font-semibold mb-2">What is your experience level?</label>
                            <select
                                id="experienceLevel"
                                name="experienceLevel"
                                value={userPreferences.experienceLevel}
                                onChange={handleInputChange}
                                className="w-full bg-white/5 border border-cyan-400/30 rounded-lg px-4 py-3 text-white focus:ring-2 focus:ring-neon-blue focus:border-neon-blue outline-none transition-all"
                            >
                                <option>Student / Entry-Level</option>
                                <option>Mid-Level Professional (2-5 years)</option>
                                <option>Senior Professional (5+ years)</option>
                            </select>
                        </div>
                    </div>

                    {/* File Upload Area */}
                    <div className="mb-6">
                        <label className="block font-semibold mb-2">Upload your resume</label>
                        <div className="relative border-2 border-dashed border-cyan-400/50 rounded-xl p-8 text-center cursor-pointer hover:border-neon-blue hover:bg-white/5 transition-all group">
                            <input
                                type="file"
                                onChange={handleFileChange}
                                className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
                                accept=".pdf,.doc,.docx,.txt"
                            />
                            <UploadIcon />
                            <p className="font-semibold text-neon-blue">
                                {resumeFile ? 'File Selected!' : 'Click to upload or drag and drop'}
                            </p>
                            <p className="text-sm text-white/60">
                                {resumeFile ? resumeFile.name : 'PDF, DOC, DOCX, or TXT'}
                            </p>
                        </div>
                    </div>

                    {/* Generate Button */}
                    <button type="submit" disabled={isLoading} className="w-full bg-neon-blue text-black font-bold py-3 px-6 rounded-lg shadow-neon-blue hover:shadow-lg hover:shadow-neon-blue transition-all duration-300 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed">
                        {isLoading ? 'Analyzing...' : 'Generate My Blueprint'}
                    </button>

                    {error && <p className="text-center text-red-400 mt-4">{error}</p>}
                </form>
            </div>
        </div>
    );
}

export default ResumeBuilder;
