import React from 'react';
import { Plus, Users, TrendingUp, LightbulbIcon, Heart, MessageSquare, Share2 } from 'lucide-react';

// Data for the sidebar elements
const trendingTopics = [
    { name: 'AI/ML Projects', posts: 234 },
    { name: 'Hackathon Teams', posts: 89 },
    { name: 'Design Critique', posts: 156 },
    { name: 'Coding Help', posts: 312 },
    { name: 'Career Advice', posts: 203 },
];

const activeContributors = [
    { name: 'Maya Patel', role: 'AI Researcher', posts: 156 },
    { name: 'David Kim', role: 'Full Stack Dev', posts: 134 },
    { name: 'Luna Torres', role: 'UX Designer', posts: 98 },
    { name: 'Ryan Wilson', role: 'Data Scientist', posts: 87 },
];

const suggestedForYou = [
    'React Performance Tips',
    'Design System Best Practices',
    'Interview Preparation',
    'Open Source Contributing',
];

export function CommunitySidebar() {
    return (
        <aside className="lg:col-span-1 space-y-8">
            {/* Trending Topics Card */}
            <div className="hologram p-6 rounded-2xl border-neon-cyan/50">
                <h3 className="font-orbitron text-xl font-bold text-white mb-4 flex items-center gap-2"><TrendingUp size={22} className="text-neon-cyan" /> Trending Topics</h3>
                <ul className="space-y-3">
                    {trendingTopics.map(topic => (
                        <li key={topic.name}>
                            <a href="#" className="flex justify-between items-center text-white/80 hover:text-neon-cyan transition-colors">
                                <span># {topic.name}</span>
                                <span className="text-xs text-white/50">{topic.posts} posts</span>
                            </a>
                        </li>
                    ))}
                </ul>
            </div>

            {/* Active Contributors Card */}
            <div className="hologram p-6 rounded-2xl border-neon-purple/50">
                <h3 className="font-orbitron text-xl font-bold text-white mb-4 flex items-center gap-2"><Users size={22} className="text-neon-purple" /> Active Contributors</h3>
                <ul className="space-y-4">
                    {activeContributors.map(user => (
                        <li key={user.name} className="flex justify-between items-center">
                            <div className="flex items-center gap-3">
                                <div className="w-10 h-10 rounded-full bg-black/50 flex items-center justify-center font-bold text-neon-purple">{user.name.split(' ').map(n => n[0]).join('')}</div>
                                <div>
                                    <p className="font-semibold text-white">{user.name}</p>
                                    <p className="text-xs text-white/70">{user.role}</p>
                                </div>
                            </div>
                            <span className="text-sm font-bold text-neon-purple">{user.posts}</span>
                        </li>
                    ))}
                </ul>
            </div>

            {/* Suggested for You Card */}
            <div className="hologram p-6 rounded-2xl border-neon-pink/50">
                <h3 className="font-orbitron text-xl font-bold text-white mb-4 flex items-center gap-2"><LightbulbIcon className="text-neon-pink" /> Suggested for You</h3>
                <div className="space-y-3">
                    {suggestedForYou.map(item => (
                        <button key={item} className="w-full text-left p-3 rounded-lg font-semibold bg-black/40 hover:bg-neon-pink/20 text-white transition-colors duration-300 border border-white/20 flex items-center gap-2">
                            <Plus size={16} /> {item}
                        </button>
                    ))}
                </div>
            </div>
        </aside>
    );
}
