import React, { useState } from 'react';
import './index.css'; // Make sure this file has your Tailwind CSS imports and custom styles

// --- Icon Imports ---
// Using the popular 'lucide-react' library for icons as seen in the new component.
// You can install it with: npm install lucide-react
import { ArrowRight, Trophy, FileText, Target, Users } from 'lucide-react';

// --- Placeholder Components ---
// These are simple placeholders for your actual components.
export const Header = ({ setPage }) => (
    <header className="bg-black/50 backdrop-blur-sm sticky top-0 z-50">
        <nav className="container mx-auto px-4 py-3 flex justify-between items-center">
            <h1 onClick={() => setPage('home')} className="text-2xl font-orbitron font-bold text-white cursor-pointer">
                Cyber<span className="text-neon-cyan">Path</span>
            </h1>
            <div className="space-x-4">
                <button onClick={() => setPage('home')} className="text-white/80 hover:text-white transition-colors">Home</button>
                <button onClick={() => setPage('opportunities')} className="text-white/80 hover:text-white transition-colors">Features</button>
                <button onClick={() => setPage('resume')} className="text-white/80 hover:text-white transition-colors">Resume Builder</button>
            </div>
        </nav>
    </header>
);

export const Footer = () => (
    <footer className="bg-black/30 border-t border-white/10 mt-12">
        <div className="container mx-auto px-4 py-6 text-center text-white/60">
            <p>&copy; {new Date().getFullYear()} CyberPath. All Rights Reserved.</p>
        </div>
    </footer>
);

export const HomePage = () => (
    <div className="text-center p-8 md:p-16 animate-fadeIn">
        <h1 className="text-5xl md:text-7xl font-orbitron font-bold text-white mb-6">
            Welcome to <span className="bg-gradient-to-r from-neon-blue via-neon-purple to-neon-pink bg-clip-text text-transparent">CyberPath</span>
        </h1>
        <p className="text-xl text-white/80 max-w-3xl mx-auto">
            Your ultimate portal for career acceleration. Discover opportunities, build your resume, and connect with a community of innovators.
        </p>
    </div>
);

// A simple placeholder for your ResumeBuilder component
export const ResumeBuilder = () => (
     <div className="text-center p-8 md:p-16 animate-fadeIn">
        <h1 className="text-5xl font-orbitron font-bold text-white mb-6">AI Resume Builder</h1>
        <p className="text-xl text-white/80">Resume building functionality will be here.</p>
    </div>
);

// --- New FeatureCategories Component (Integrated) ---
const categories = [
  {
    id: "competitions",
    title: "Competition Aggregator",
    description: "Join exciting challenges and showcase your skills.",
    icon: Trophy,
    neonColor: "neon-blue",
    count: "2,340+",
    trending: "+12 today",
    items: [
      { title: "AI/ML Challenge 2024", company: "Meta", prize: "$10K" },
      { title: "Cyber Wars Hackathon", company: "Google", prize: "$5K" },
      { title: "Blockchain Case Study", company: "Coinbase", prize: "$15K" }
    ]
  },
  {
    id: "resume", // This ID matches the 'page' state for navigation
    title: "Resume Builder",
    description: "AI-powered tools to create the perfect resume.",
    icon: FileText,
    neonColor: "neon-purple",
    count: "1,890+",
    trending: "+7 today",
    items: [
      { title: "AI Resume Builder", provider: "PathFinder", type: "Tool" },
      { title: "Mock Interviews", provider: "CyberPrep", type: "Practice" },
      { title: "Portfolio Review", provider: "DigitalCrit", type: "Feedback" }
    ]
  },
  {
    id: "practice",
    title: "Practice Sessions",
    description: "Skill-building sessions with expert guidance.",
    icon: Target,
    neonColor: "neon-green",
    count: "1,250+",
    trending: "+8 today",
    items: [
      { title: "Coding Practice", provider: "CodeCyber", spots: "20 left" },
      { title: "Interview Prep", provider: "PrepCyber", spots: "15 left" },
      { title: "Presentation Skills", provider: "SpeakCyber", spots: "30 left" }
    ]
  },
  {
    id: "community",
    title: "Community Platform",
    description: "Connect, collaborate, and grow with peers.",
    icon: Users,
    neonColor: "neon-pink",
    count: "3,420+",
    trending: "+18 today",
    items: [
      { title: "Study Groups", commitment: "2h/week" },
      { title: "Mentorship Program", commitment: "1h/week" },
      { title: "Skill Exchange", commitment: "Flexible" }
    ]
  }
];

export function FeatureCategories({ setPage }) {
  // The handler for the button clicks
  const handleNavigation = (pageId) => {
    // Navigate if the page exists in our app's routing
    if (pageId === 'resume') {
        setPage(pageId);
    } else {
        // For other categories, you can add their pages later.
        console.log(`Navigation to "${pageId}" is not yet implemented.`);
        alert(`The "${pageId}" page is coming soon!`);
    }
  };

  return (
    <section className="py-20 bg-black relative overflow-hidden animate-fadeIn">
      <div className="absolute inset-0 cyber-grid opacity-20"></div>
      <div className="container mx-auto px-4 relative z-10">
        <div className="text-center mb-16">
          <h2 className="font-orbitron text-4xl md:text-5xl font-bold mb-6 text-white neon-text">
            Explore Our <span className="bg-gradient-to-r from-neon-blue via-neon-purple to-neon-pink bg-clip-text text-transparent">
              Cyber Features
            </span>
          </h2>
          <p className="text-xl text-white/80 max-w-3xl mx-auto leading-relaxed">
            From competitions to skill building, discover all the digital tools you need to accelerate your career journey.
          </p>
        </div>
        <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
          {categories.map((category) => {
            const Icon = category.icon;
            return (
              <div key={category.id} className={`group flex flex-col hover:shadow-2xl transition-all duration-500 border-0 shadow-xl hologram hover:scale-105 hover:-translate-y-2 rounded-2xl overflow-hidden hover:shadow-${category.neonColor}`}>
                <div className={`h-2 bg-gradient-to-r from-${category.neonColor} to-transparent`}></div>

                <div className="p-6 flex-grow flex flex-col">
                  <div className="flex items-start justify-between mb-4">
                    <div className={`p-3 inline-block rounded-xl bg-${category.neonColor}/20 border border-${category.neonColor}/30 shadow-lg transform group-hover:scale-110 transition-all duration-300`}>
                      <Icon className="h-8 w-8 text-white" />
                    </div>
                    <div className="text-right">
                      <div className={`text-lg font-bold text-${category.neonColor} neon-text`}>{category.count}</div>
                      <div className="px-2 py-0.5 text-xs rounded-full bg-gradient-to-r from-neon-green to-neon-cyan text-black border-0 font-semibold">
                        {category.trending}
                      </div>
                    </div>
                  </div>

                  <h3 className="font-orbitron text-xl font-bold text-white mb-2">{category.title}</h3>
                  <p className="text-white/80 leading-relaxed mb-6">{category.description}</p>

                  <div className="space-y-3 mt-auto pt-6 border-t border-white/10">
                    {category.items.map((item, index) => (
                      <div key={index} className="bg-black/30 backdrop-blur-sm rounded-lg p-3 border border-white/10 text-xs">
                        <div className="font-semibold text-white mb-1 truncate">{item.title}</div>
                        <div className="flex items-center justify-between text-white/70">
                          <span>{item.company || item.provider}</span>
                          <span className={`font-bold text-${category.neonColor}`}>{item.prize || item.spots || item.type || item.commitment}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="p-6 pt-0 mt-4">
                  <button onClick={() => handleNavigation(category.id)} className={`w-full bg-gradient-to-r from-${category.neonColor} to-${category.neonColor}/80 hover:shadow-xl text-white border-0 rounded-xl py-3 font-semibold transform hover:scale-105 transition-all duration-300 neon-text`}>
                    Explore Feature <ArrowRight className="inline ml-2 h-4 w-4" />
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
}


// --- Main App Component ---
function App() {
    const [page, setPage] = useState('home'); // Default to the home page

    // This function determines which page component to render based on the 'page' state.
    const renderPage = () => {
        switch (page) {
            case 'home':
                return <HomePage />;
            case 'opportunities': // This now renders the new FeatureCategories component
                return <FeatureCategories setPage={setPage} />;
            case 'resume':
                return <ResumeBuilder />;
            // You can add more cases here for 'competitions', 'practice', etc. as you build them.
            // Example:
            // case 'competitions':
            //     return <CompetitionsPage />;
            default:
                return <HomePage />;
        }
    };

    return (
        <div className="flex flex-col min-h-screen bg-black text-white">
            <Header setPage={setPage} />
            <main className="flex-grow">
                <div className="container px-4 mx-auto">
                    {renderPage()}
                </div>
            </main>
            <Footer />
        </div>
    );
}

export default App;
