import React, { useState, useEffect } from 'react';
import './index.css';

// Import all the new and existing components
import { Header } from './Header';
import { Footer } from './Footer';
import { HomePage } from './Homepage';
import ResumeBuilder from './Resumebuilder'; // Make sure this filename matches yours

// This is your original Opportunities page, now styled with the new theme
function OpportunitiesPage({ onSelectCategory, selectedCategory, onBack, opportunities, loading, error }) {
    const colorClasses = {
        cyan: 'border-neon-cyan/50 hover:shadow-neon-cyan/50',
        purple: 'border-neon-purple/50 hover:shadow-neon-purple/50',
        pink: 'border-neon-pink/50 hover:shadow-neon-pink/50',
    };
    const categories = [
        { name: "Internship", icon: "üíº", description: "Gain practical experience.", color: "cyan" },
        { name: "Competition", icon: "üèÜ", description: "Battle for excellence.", color: "purple" },
        { name: "Webinar", icon: "üíª", description: "Learn from the experts.", color: "pink" },
        // ... add more categories if you have them
    ];

    if (loading) return <p className="p-8 text-lg text-center text-white/70">Loading opportunities...</p>;
    if (error) return <p className="p-8 text-lg text-center text-red-400">Error: {error}. Is your server running?</p>;

    if (selectedCategory) {
        const filteredOps = opportunities.filter(op => op.type === selectedCategory);
        return (
             <div className="p-4 md:p-8">
                <button onClick={onBack} className="px-4 py-2 mb-8 font-semibold text-white transition-all duration-300 rounded-md bg-white/10 hover:bg-white/20">&larr; Back to Categories</button>
                <h2 className="mb-8 text-3xl font-bold text-center text-white font-orbitron">{selectedCategory} Opportunities</h2>
                <div className="grid grid-cols-1 gap-6 md:grid-cols-2 lg:grid-cols-3">
                    {filteredOps.map(op => (
                        <div key={op.id} className="p-6 transition-all duration-300 border rounded-lg bg-black/50 border-purple-400/30 backdrop-blur-sm hover:border-purple-400/70 hover:scale-105">
                            <h3 className="mb-2 text-xl font-bold text-neon-purple">{op.title}</h3>
                            <p className="text-white/80">{op.description}</p>
                        </div>
                    ))}
                </div>
            </div>
        );
    }

    return (
        <div className="p-4 text-center md:p-8">
            <h1 className="mb-12 text-4xl font-bold text-white font-orbitron">Select an Opportunity Category</h1>
            <div className="grid grid-cols-1 gap-8 md:grid-cols-3">
                {categories.map(cat => (
                    <div key={cat.name} onClick={() => onSelectCategory(cat.name)} className={`p-6 bg-black/50 border ${colorClasses[cat.color]} backdrop-blur-sm rounded-xl cursor-pointer hover:-translate-y-2 transition-transform group`}>
                        <div className="mb-4 text-4xl transition-transform duration-300 group-hover:scale-110">{cat.icon}</div>
                        <h3 className="text-2xl font-bold font-orbitron">{cat.name}</h3>
                    </div>
                ))}
            </div>
        </div>
    );
}


// Main App Component
function App() {
    const [page, setPage] = useState('home'); // Default to the new home page
    const [opportunities, setOpportunities] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState(null);

    useEffect(() => {
        // This fetch URL is for development mode (2 terminals)
        fetch('http://localhost:3001/api/opportunities')
            .then(res => res.json())
            .then(data => {
                setOpportunities(data);
                setLoading(false);
            })
            .catch(err => {
                setError(err.message);
                setLoading(false);
            });
    }, []);

    const renderPage = () => {
        switch (page) {
            case 'home':
                return <HomePage />;
            case 'opportunities':
                return <OpportunitiesPage 
                            selectedCategory={selectedCategory}
                            onSelectCategory={setSelectedCategory}
                            onBack={() => setSelectedCategory(null)}
                            opportunities={opportunities}
                            loading={loading}
                            error={error}
                        />;
            case 'resume':
                return <ResumeBuilder />;
            default:
                return <HomePage />;
        }
    };

    return (
        <div className="flex flex-col min-h-screen">
            <Header setPage={setPage} />
            <main className="flex-grow">
                <div className="container px-4 mx-auto">
                    {renderPage()}
                </div>
            </main>
            <Footer />
        </div>
    );
}

export default App;
