// src/ResumeBuilder.js - The Component for the AI Resume Builder Feature

import React, { useState } from 'react';

function ResumeBuilder() {
  // State to hold the user's form inputs
  const [dreamJob, setDreamJob] = useState('');
  const [resumeText, setResumeText] = useState('');
  const [experience, setExperience] = useState('entry'); // default value
  const [workStyle, setWorkStyle] = useState('any'); // default value

  // State to hold the results from the backend
  const [analysisResult, setAnalysisResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = (event) => {
    event.preventDefault(); // Prevent the browser from reloading the page
    setIsLoading(true);
    setError(null);
    setAnalysisResult(null);

    // This is the "waiter" taking the user's "order" (their details) to the "kitchen" (server.js)
    fetch('/api/opportunities', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        resumeText: resumeText,
        userPreferences: {
          dreamJob: dreamJob,
          experience: experience,
          workStyle: workStyle,
        },
      }),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Something went wrong. Please check if your server is running.');
        }
        return response.json();
      })
      .then(data => {
        setAnalysisResult(data); // Save the result from the backend
        setIsLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setIsLoading(false);
      });
  };

  return (
    <div className="resume-builder-container">
      <h2>AI-Powered Resume & Career Blueprint</h2>
      <p className="subtitle">
        Fill out your details, paste your current resume, and let our AI craft the perfect resume and suggest the next steps for your career.
      </p>

      {/* --- The Form --- */}
      <form onSubmit={handleSubmit} className="resume-form">
        <div className="form-grid">
          <div className="form-group">
            <label htmlFor="dream-job">What is your dream job?</label>
            <input
              type="text"
              id="dream-job"
              value={dreamJob}
              onChange={(e) => setDreamJob(e.target.value)}
              placeholder="e.g., Software Engineer, Product Manager"
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="experience">What is your experience level?</label>
            <select id="experience" value={experience} onChange={(e) => setExperience(e.target.value)}>
              <option value="entry">Student / Entry-Level</option>
              <option value="mid">Mid-Level (2-5 years)</option>
              <option value="senior">Senior (5+ years)</option>
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="work-style">Do you prefer online or offline opportunities?</label>
            <select id="work-style" value={workStyle} onChange={(e) => setWorkStyle(e.target.value)}>
              <option value="any">Any</option>
              <option value="online">Online / Remote</option>
              <option value="offline">Offline / In-Person</option>
            </select>
          </div>
        </div>
        <div className="form-group">
          <label htmlFor="resume-text">Paste your current resume text here:</label>
          <textarea
            id="resume-text"
            rows="12"
            value={resumeText}
            onChange={(e) => setResumeText(e.target.value)}
            placeholder="e.g., John Doe - Responsible for making a website..."
            required
          ></textarea>
        </div>
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Analyzing...' : 'Generate My Blueprint'}
        </button>
      </form>

      {/* --- The Results Section --- */}
      {error && <div className="error-message">{error}</div>}

      {analysisResult && (
        <div className="results-container">
          <h3>Your Personalized Blueprint</h3>

          <div className="result-section">
            <h4>üìù Your "Picture-Perfect" Resume</h4>
            <div className="resume-output">
              <pre><strong>Header:</strong><br/>{analysisResult.improvedResume.header}</pre>
              <pre><strong>Summary:</strong><br/>{analysisResult.improvedResume.summary}</pre>
              <pre><strong>Experience:</strong><br/>{analysisResult.improvedResume.experience.join('\n')}</pre>
              <pre><strong>Skills:</strong><br/>{analysisResult.improvedResume.skills}</pre>
            </div>
          </div>

          <div className="result-section">
            <h4>üí° Actionable Suggestions</h4>
            <ul className="suggestions-list">
              {analysisResult.suggestions.map((suggestion, index) => (
                <li key={index}>{suggestion}</li>
              ))}
            </ul>
          </div>

          <div className="result-section">
            <h4>üöÄ Recommended Opportunities</h4>
            <div className="opportunities-list">
              {analysisResult.recommendedOpportunities.length > 0 ? (
                analysisResult.recommendedOpportunities.map(op => {
                   const typeClass = op.type.split(' ')[0].toLowerCase();
                   const cardClassName = `opportunity-card card-${typeClass}`;
                   return (
                     <div className={cardClassName} key={op.id}>
                       <h3>{op.title} <span className="opportunity-type">({op.type})</span></h3>
                       <p>{op.description}</p>
                       <a href={op.url} target="_blank" rel="noopener noreferrer">Learn More</a>
                     </div>
                   );
                })
              ) : (
                <p>No specific opportunities found based on your profile, but we're always adding more!</p>
              )}
            </div>
          </div>

        </div>
      )}
    </div>
  );
}

export default ResumeBuilder;
