// src/pages/AIFriend.js
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

// Mentor data
const mentors = [
  {
    type: "Tech Mentor",
    name: "Alex",
    color: "#4ade80",
    description: "Expert in software development, AI, and cloud technologies."
  },
  {
    type: "Design Mentor",
    name: "Sophia",
    color: "#f472b6",
    description: "Creative designer specializing in UX/UI and digital art."
  },
  {
    type: "Career Coach",
    name: "Raj",
    color: "#60a5fa",
    description: "Helping professionals identify strengths and plan their careers."
  },
  {
    type: "Entrepreneur Guide",
    name: "Maya",
    color: "#facc15",
    description: "Experienced startup founder and business strategist."
  },
  {
    type: "Public Speaking Coach",
    name: "Liam",
    color: "#a78bfa",
    description: "Coach in communication, leadership, and presentation skills."
  },
  {
    type: "Dance Coach",
    name: "Nina",
    color: "#f87171",
    description: "Professional dancer guiding you through choreography and expression."
  },
  {
    type: "Sports Coach",
    name: "Karan",
    color: "#34d399",
    description: "Trains in fitness, agility, and sports strategy."
  },
  {
    type: "Music Mentor",
    name: "Elena",
    color: "#60a5fa",
    description: "Expert in music theory, instruments, and vocal coaching."
  }
];

const AIFriend = () => {
  const [selectedMentor, setSelectedMentor] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim() || !selectedMentor) return;

    const newMsg = { role: "user", content: input };
    setMessages((prev) => [...prev, newMsg]);
    setInput("");

    try {
      const res = await axios.post("http://localhost:3001/api/mentor/chat", {
        userId: "user123",
        mentorType: selectedMentor.type,
        message: input,
      });
      setMessages(res.data.messages);
    } catch (err) {
      console.error("Error sending message:", err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-black via-purple-950 to-black text-white font-orbitron flex flex-col items-center py-10">
      <h1 className="text-4xl font-bold text-neon-purple mb-6">ðŸ¤– AI Friend</h1>

      {/* Mentor Selection */}
      {!selectedMentor && (
        <div className="flex justify-around w-full max-w-7xl mb-6 flex-wrap">
          {mentors.map((mentor) => (
            <div
              key={mentor.type}
              onClick={() => setSelectedMentor(mentor)}
              className="cursor-pointer flex flex-col items-center bg-black/70 border border-neon-blue rounded-2xl p-4 hover:scale-105 transform transition-all duration-300 max-w-[200px] mb-4"
            >
              <div
                className="w-24 h-24 rounded-full flex items-center justify-center text-3xl font-bold text-black mb-2"
                style={{ backgroundColor: mentor.color }}
              >
                {mentor.name.charAt(0)}
              </div>
              <span className="text-lg font-semibold">{mentor.name}</span>
              <span className="text-sm text-neon-blue mb-2">{mentor.type}</span>
              <p className="text-xs text-gray-300 text-center mb-2">
                {mentor.description}
              </p>
              <span className="text-sm text-neon-purple font-medium text-center">
                ðŸ’¬ Have a chat with me to figure out your path
              </span>
            </div>
          ))}
        </div>
      )}

      {/* Chat Window */}
      {selectedMentor && (
        <>
          <div className="mb-4 flex items-center w-full max-w-3xl">
            <div
              className="w-12 h-12 rounded-full flex items-center justify-center text-lg font-bold text-black mr-3"
              style={{ backgroundColor: selectedMentor.color }}
            >
              {selectedMentor.name.charAt(0)}
            </div>
            <h2 className="text-2xl font-bold text-neon-purple">
              {selectedMentor.name} ({selectedMentor.type})
            </h2>
            <button
              className="ml-auto text-sm text-neon-blue underline"
              onClick={() => {
                setSelectedMentor(null);
                setMessages([]);
              }}
            >
              Change Mentor
            </button>
          </div>

          <div className="w-full max-w-3xl bg-black/70 border border-neon-blue rounded-2xl shadow-lg p-6 overflow-y-auto h-[60vh] mb-6">
            {messages.map((msg, i) => (
              <div
                key={i}
                className={`my-3 flex ${
                  msg.role === "user" ? "justify-end" : "justify-start"
                }`}
              >
                <div
                  className={`max-w-[75%] px-4 py-2 rounded-2xl text-sm shadow-md ${
                    msg.role === "user"
                      ? "bg-neon-blue text-black"
                      : "bg-neon-purple text-white"
                  }`}
                >
                  {msg.content}
                </div>
              </div>
            ))}
            <div ref={chatEndRef} />
          </div>

          <form
            onSubmit={sendMessage}
            className="w-full max-w-3xl flex items-center space-x-3"
          >
            <input
              type="text"
              placeholder={`Ask ${selectedMentor.name} something...`}
              className="flex-grow bg-black/60 border border-neon-blue text-white px-4 py-3 rounded-2xl focus:outline-none focus:ring-2 focus:ring-neon-purple"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
            <button
              type="submit"
              className="bg-neon-purple hover:bg-neon-blue text-white px-6 py-3 rounded-2xl font-semibold transition-all duration-300"
            >
              Send
            </button>
          </form>
        </>
      )}
    </div>
  );
};

export default AIFriend;
