import React, { useState, useEffect } from 'react';
import './index.css';
import { Header } from './Header';
import { Footer } from './Footer';
import { HomePage } from './Homepage';
import ResumeBuilder from './Resumebuilder';
import { ArrowRight } from 'lucide-react';
import { TrophyIcon, BookIcon, TargetIcon, UsersIcon } from './Iconcomponents';
import { TrajectoryBuilder } from './TrajectoryBuilder';
import { DashboardPage } from './Dashboard';
import PracticePage from './Practicesession';
import AIFriend from './AIFriend';


// Mapping for neon colors to Tailwind classes
const neonTextClasses = {
  "neon-blue": "text-neon-blue",
  "neon-purple": "text-neon-purple",
  "neon-green": "text-neon-green",
  "neon-pink": "text-neon-pink",
  "neon-cyan": "text-neon-cyan",
};

const neonBgClasses = {
  "neon-blue": "bg-neon-blue/20 border-neon-blue/30",
  "neon-purple": "bg-neon-purple/20 border-neon-purple/30",
  "neon-green": "bg-neon-green/20 border-neon-green/30",
  "neon-pink": "bg-neon-pink/20 border-neon-pink/30",
  "neon-cyan": "bg-neon-cyan/20 border-neon-cyan/30",
};

const neonGradientClasses = {
  "neon-blue": "from-neon-blue",
  "neon-purple": "from-neon-purple",
  "neon-green": "from-neon-green",
  "neon-pink": "from-neon-pink",
  "neon-cyan": "from-neon-cyan",
};

function OpportunitiesPage() {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch('/api/opportunities')
      .then(res => res.json())
      .then(data => {
        console.log("✅ Loaded opportunities from backend:", data);
        setCategories([
          {
            id: "competitions",
            title: "Competition Aggregator",
            description: "Join exciting challenges and showcase your skills",
            icon: TrophyIcon,
            neonColor: "neon-blue",
            count: `${data.length}+`,
            trending: "+12 today",
            items: [
              { title: "NASA Space Apps Challenge", company: "NASA", prize: "Innovation Award" },
              { title: "Google Code Jam", company: "Google", prize: "Cash Prize" },
              { title: "UNICEF Youth Challenge", company: "UNICEF", prize: "Incubation Support" },
            ],
          },
          {
            id: "courses",
            title: "Learning Hub",
            description: "Explore online and offline courses to boost your skills",
            icon: BookIcon,
            neonColor: "neon-purple",
            count: "3,200+",
            trending: "+12 today",
            items: [
              { title: "Full-Stack Web Development", provider: "Coursera", type: "Online Course" },
              { title: "Creative Writing Workshop", provider: "LitWorks", type: "Offline Course" },
              { title: "AI for Everyone", provider: "edX", type: "Online Course" },
            ],
          },
          {
            id: "seminars",
            title: "Seminars & Webinars",
            description: "Interactive learning sessions with industry experts",
            icon: TargetIcon,
            neonColor: "neon-green",
            count: "980+",
            trending: "+12 today",
            items: [
              { title: "Tech Innovations Summit", provider: "InnoCyber", spots: "50 left" },
              { title: "Sustainability in Action", provider: "GreenTech", spots: "80 left" },
              { title: "Entrepreneurship Bootcamp", provider: "Startup India", spots: "30 left" },
            ],
          },
          {
            id: "community",
            title: "Community Service",
            description: "Engage in meaningful service and make a positive impact",
            icon: UsersIcon,
            neonColor: "neon-pink",
            count: "2,750+",
            trending: "+10 today",
            items: [
              { title: "Beach Clean-Up Drive", commitment: "4h/month" },
              { title: "Tree Plantation Campaign", commitment: "Weekend Volunteering" },
              { title: "Food Distribution Initiative", commitment: "Weekly" },
            ],
          },
        ]);
      });
  }, []);

  return (
    <section className="py-20 bg-black relative overflow-hidden">
      <div className="absolute inset-0 cyber-grid opacity-20"></div>
      <div className="container mx-auto px-4 relative z-10">
        <div className="text-center mb-16">
          <h2 className="font-orbitron text-4xl md:text-5xl font-bold mb-6 text-white neon-text">
            Explore Our <span className="bg-gradient-to-r from-neon-blue via-neon-purple to-neon-pink bg-clip-text text-transparent">
              Cyber Features
            </span>
          </h2>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
          {categories.map((category) => {
            const Icon = category.icon;
            return (
              <div key={category.id}
                className="group flex flex-col hover:shadow-2xl border-0 shadow-xl hologram rounded-2xl overflow-hidden hover:scale-105 transition-all duration-500">
                <div className={`h-2 bg-gradient-to-r ${neonGradientClasses[category.neonColor]} to-transparent`}></div>
                <div className="p-6 flex-grow flex flex-col">
                  <div className="flex items-start justify-between mb-4">
                    <div className={`p-3 rounded-xl ${neonBgClasses[category.neonColor]}`}>
                      <Icon className="h-8 w-8" />
                    </div>
                    <div className="text-right">
                      <div className={`text-lg font-bold ${neonTextClasses[category.neonColor]}`}>{category.count}</div>
                      <div className="px-2 py-0.5 text-xs rounded-full bg-gradient-to-r from-neon-green to-neon-cyan text-black font-semibold">
                        {category.trending}
                      </div>
                    </div>
                  </div>
                  <h3 className="font-orbitron text-xl font-bold text-white mb-2">{category.title}</h3>
                  <p className="text-white/80 mb-6">{category.description}</p>
                  <div className="space-y-3 mt-auto pt-6 border-t border-white/10">
                    {category.items.map((item, i) => (
                      <div key={i} className="bg-black/30 backdrop-blur-sm rounded-lg p-3 border border-white/10 text-xs">
                        <div className="font-semibold text-white mb-1 truncate">{item.title}</div>
                        <div className="flex justify-between text-white/70">
                          <span>{item.company || item.provider}</span>
                          <span className={`font-bold ${neonTextClasses[category.neonColor]}`}>
                            {item.prize || item.type || item.commitment}
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
                <div className="p-6 pt-0 mt-4">
                  <button className={`w-full bg-gradient-to-r ${neonGradientClasses[category.neonColor]} to-${category.neonColor}/80 text-white rounded-xl py-3 font-semibold`}>
                    Explore Feature <ArrowRight className="inline ml-2 h-4 w-4" />
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
}

function App() {
  const [page, setPage] = useState('home');

  const renderPage = () => {
    switch (page) {
      case 'home': return <HomePage />;
      case 'dashboard': return <DashboardPage />;
      case 'opportunities': return <OpportunitiesPage />;
      case 'resume': return <ResumeBuilder />;
      case 'trajectory-builder': return <TrajectoryBuilder />;
      case 'AIfriend': return <AIFriend />;
      case 'practice': return <PracticePage />;
      default: return <HomePage />;
    }
  };

  return (
    <div className="flex flex-col min-h-screen">
      <Header setPage={setPage} />
      <main className="flex-grow">{renderPage()}</main>
      <Footer />
    </div>
  );
}

export default App;
