// src/components/FeatureCard.jsx
import React, { useRef } from 'react';
import { motion } from 'framer-motion';
import { ArrowRight } from 'lucide-react';

// Animation variants for Framer Motion
const itemVariants = {
  hidden: { opacity: 0, y: 50, scale: 0.9 },
  visible: { 
    opacity: 1, 
    y: 0, 
    scale: 1,
    transition: {
      type: "spring",
      stiffness: 100,
      damping: 10,
    }
  }
};

export function FeatureCard({ id, title, description, icon: IconComponent, neonColor }) {
  const cardRef = useRef(null);

  const handleMouseMove = (e) => {
    const card = cardRef.current;
    if (!card) return;

    const { left, top, width, height } = card.getBoundingClientRect();
    const x = e.clientX - left;
    const y = e.clientY - top;

    // Set mouse position for the glow effect
    card.style.setProperty('--mouse-x', `${x}px`);
    card.style.setProperty('--mouse-y', `${y}px`);

    // Calculate rotation for 3D effect
    const rotateX = (y - height / 2) / 20; // Tweak divisor for sensitivity
    const rotateY = (x - width / 2) / -20; // Tweak divisor for sensitivity
    card.style.transform = `rotateX(${rotateX}deg) rotateY(${rotateY}deg) scale3d(1.05, 1.05, 1.05)`;
  };

  const handleMouseLeave = () => {
    const card = cardRef.current;
    if (card) {
      card.style.transform = 'rotateX(0deg) rotateY(0deg) scale3d(1, 1, 1)';
    }
  };

  return (
    <motion.div
      variants={itemVariants}
      className="card-container"
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
    >
      <div
        ref={cardRef}
        className={`hologram-card relative group bg-black/40 backdrop-blur-sm border border-white/10 rounded-2xl overflow-hidden transition-all duration-300 hover:border-${neonColor}/80 hover:shadow-${neonColor}`}
      >
        <div className="card-content p-8">
          <div className={`p-4 inline-block rounded-2xl bg-${neonColor}/20 border border-${neonColor}/30 shadow-lg transform group-hover:scale-110 group-hover:rotate-6 transition-all duration-300 mb-6`}>
            <IconComponent className="h-8 w-8" style={{ color: `var(--color-${neonColor})` }} />
          </div>
          <h3 className="font-orbitron text-xl font-bold text-white mb-3">{title}</h3>
          <p className="text-white/80 leading-relaxed mb-6 h-20">{description}</p>
          <button className={`w-full relative overflow-hidden bg-${neonColor}/80 hover:bg-${neonColor} text-white border-0 rounded-xl py-3 font-semibold transform hover:scale-105 transition-all duration-300`}>
            <span className="relative z-10 flex items-center justify-center">
              Explore Now <ArrowRight className="inline ml-2 h-4 w-4" />
            </span>
          </button>
        </div>
      </div>
    </motion.div>
  );
}
