// src/pages/AIFriend.js
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import { Send, MessageCircle, Zap } from "lucide-react";

const mentors = [
  { type: "Tech Mentor", name: "Fazal", color: "#4ade80", description: "Expert in software development, AI, and cloud technologies." },
  { type: "Design Mentor", name: "Alaana", color: "#f472b6", description: "Creative designer specializing in UX/UI and digital art." },
  { type: "Career Coach", name: "Khaliq and Shafiq", color: "#60a5fa", description: "Helping professionals identify strengths and plan their careers." },
  { type: "Entrepreneur Guide", name: "Omar", color: "#facc15", description: "Experienced startup founder and business strategist." },
];

const AIFriend = () => {
  const [selectedMentor, setSelectedMentor] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim() || !selectedMentor) return;

    const newMsg = { role: "user", content: input, time: new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }) };
    setMessages((prev) => [...prev, newMsg]);
    setInput("");

    try {
      const res = await axios.post("http://localhost:3001/api/mentor/chat", {
        userId: "user123",
        mentorType: selectedMentor.type,
        message: input,
      });
      setMessages(res.data.messages);
    } catch (err) {
      console.error("Error sending message:", err);
    }
  };

  return (
    <div className="min-h-screen bg-black flex flex-col md:flex-row text-white p-8 md:space-x-8 font-sans">
      {/* Left Panel */}
      <motion.div
        initial={{ x: -20, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.6 }}
        className="w-full md:w-1/3 bg-black/50 border border-gray-800 rounded-2xl p-6 backdrop-blur-xl shadow-lg flex flex-col items-center text-center"
      >
        <div className="flex flex-col items-center mb-4">
          <motion.div
            className="w-20 h-20 rounded-full flex items-center justify-center text-3xl font-bold text-black"
            style={{
              background: selectedMentor ? selectedMentor.color : "#60a5fa",
              boxShadow: `0 0 25px ${selectedMentor ? selectedMentor.color : "#60a5fa"}70`,
            }}
            whileHover={{ scale: 1.1, rotate: 5 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            {selectedMentor ? selectedMentor.name.charAt(0) : "A"}
          </motion.div>
          <h2 className="mt-4 text-2xl font-semibold tracking-wide">{selectedMentor ? selectedMentor.name : "Your AI Friend"}</h2>
          <p className="text-gray-400 text-sm">{selectedMentor ? selectedMentor.type : "Choose a mentor to begin"}</p>
        </div>

        {selectedMentor ? (
          <>
            <p className="text-gray-300 text-sm mb-6">{selectedMentor.description}</p>
            <button onClick={() => setMessages([])} className="w-full border border-gray-700 text-gray-300 rounded-xl py-2 mb-3 hover:bg-gray-800 hover:text-white transition">
              ðŸ’¬ New Chat
            </button>
            <button onClick={() => setSelectedMentor(null)} className="w-full border border-gray-700 text-gray-300 rounded-xl py-2 hover:bg-gray-800 hover:text-white transition">
              ðŸ”„ Change Mentor
            </button>
          </>
        ) : (
          <div className="w-full space-y-3">
            <p className="text-gray-400 text-sm">Select a mentor to start chatting:</p>
            {mentors.map((m) => (
              <motion.button
                key={m.name}
                whileHover={{ scale: 1.03, backgroundColor: "#1a1a1d" }}
                className="w-full border border-gray-700 text-gray-300 rounded-xl py-3 transition text-sm"
                onClick={() => setSelectedMentor(m)}
              >
                {m.name} â€” {m.type}
              </motion.button>
            ))}
          </div>
        )}
      </motion.div>

      {/* Right Panel */}
      <motion.div
        initial={{ x: 20, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.6 }}
        className="w-full md:w-2/3 flex flex-col bg-[#0d0d0f] border border-gray-800 rounded-2xl shadow-2xl relative overflow-hidden"
      >
        {/* --- AURA Section with Effects --- */}
        <motion.div
          className="relative border-b border-gray-800 p-6 bg-gradient-to-r from-[#10131a] to-[#0d0d0f]"
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.7 }}
        >
          {/* Subtle glow background */}
          <div className="absolute inset-0 bg-gradient-to-r from-blue-500/10 via-purple-500/10 to-transparent blur-2xl opacity-60 pointer-events-none"></div>

          <div className="flex justify-between items-center relative z-10">
            <div>
              <h2 className="text-3xl font-bold bg-gradient-to-r from-blue-400 via-sky-300 to-purple-400 bg-clip-text text-transparent">
                AURA
              </h2>
              <p className="text-gray-400 text-sm tracking-wide">Career Coach</p>
            </div>
            <button className="text-gray-400 text-sm hover:text-white transition">âœ– Close Chat</button>
          </div>

          {/* Quick Actions */}
          <motion.div
            className="relative mt-5 bg-[#10131a]/80 border border-gray-800 rounded-2xl p-4 backdrop-blur-lg"
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.5 }}
          >
            <h3 className="text-gray-300 text-sm mb-3 flex items-center gap-2">
              âš¡ Quick Actions
            </h3>
            <div className="grid grid-cols-2 sm:flex sm:flex-wrap gap-3">
              {["Create Resume", "Schedule Mock Interview", "Find Internships", "Review Applications"].map((action, idx) => (
                <motion.button
                  key={action}
                  whileHover={{ scale: 1.05, boxShadow: "0 0 10px rgba(96,165,250,0.5)" }}
                  transition={{ type: "spring", stiffness: 200 }}
                  className="flex items-center gap-2 px-4 py-2 text-sm rounded-xl bg-[#16181c] border border-gray-700 hover:bg-[#1d1f25] transition"
                >
                  <Zap size={15} className="text-sky-400" /> {action}
                </motion.button>
              ))}
            </div>
          </motion.div>
        </motion.div>

        {/* --- Chat Messages --- */}
        <div className="flex-grow overflow-y-auto px-6 py-6 space-y-6 relative z-10">
          {selectedMentor ? (
            messages.length > 0 ? (
              messages.map((msg, i) => (
                <motion.div
                  key={i}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3 }}
                  className={`flex ${msg.role === "user" ? "justify-end" : "justify-start"}`}
                >
                  <div
                    className={`relative max-w-[70%] px-4 py-3 rounded-2xl text-sm leading-relaxed shadow-lg ${
                      msg.role === "user"
                        ? "bg-gradient-to-r from-blue-600 to-blue-700 text-white"
                        : "bg-[#1e1e22] text-gray-100 border border-gray-800"
                    }`}
                  >
                    {msg.content}
                    <span className="block text-[10px] text-gray-400 mt-1 text-right">
                      {msg.time || "09:59 PM"}
                    </span>
                  </div>
                </motion.div>
              ))
            ) : (
              <div className="text-gray-500 text-center mt-20 text-sm">
                <MessageCircle className="mx-auto mb-2 text-gray-600" />
                Say hi to {selectedMentor.name} to begin your chat!
              </div>
            )
          ) : (
            <div className="text-gray-500 text-center mt-20 text-sm">
              <MessageCircle className="mx-auto mb-2 text-gray-600" />
              Select a mentor from the left to start chatting!
            </div>
          )}
          <div ref={chatEndRef} />
        </div>

        {/* --- Chat Input --- */}
        {selectedMentor && (
          <form
            onSubmit={sendMessage}
            className="flex items-center gap-3 border-t border-gray-800 bg-[#0d0d0f]/90 px-4 py-3 relative z-10"
          >
            <input
              type="text"
              placeholder={`Ask ${selectedMentor.name} anything...`}
              className="flex-grow bg-[#1a1a1d] border border-gray-700 rounded-xl text-gray-200 px-4 py-3 text-sm focus:outline-none focus:ring-2 focus:ring-sky-500 placeholder-gray-500"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
              type="submit"
              className="bg-gradient-to-r from-blue-500 to-sky-400 hover:from-blue-600 hover:to-sky-500 text-white p-3 rounded-xl transition-all shadow-lg"
            >
              <Send className="w-5 h-5" />
            </motion.button>
          </form>
        )}
      </motion.div>
    </div>
  );
};

export default AIFriend;
