// src/pages/AIFriend.js
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { motion, AnimatePresence } from "framer-motion";
import { Send, MessageCircle, Zap } from "lucide-react";

/* ================= MENTORS ================= */
const mentors = [
  {
    id: "fazal",
    name: "Fazal",
    type: "Tech Mentor",
    color: "#4ade80",
    description: "Expert in software development, AI, and cloud technologies.",
    actions: ["Create Resume", "Mock Interview", "System Design Help"],
  },
  {
    id: "alaana",
    name: "Alaana",
    type: "Design Mentor",
    color: "#f472b6",
    description: "Creative designer specializing in UX/UI and digital art.",
    actions: ["Portfolio Review", "UI Critique", "Design Career Advice"],
  },
  {
    id: "khaliq",
    name: "Khaliq & Shafiq",
    type: "Career Coach",
    color: "#60a5fa",
    description: "Helping professionals identify strengths and plan careers.",
    actions: ["Career Roadmap", "Interview Prep", "Job Strategy"],
  },
  {
    id: "omar",
    name: "Omar",
    type: "Entrepreneur Guide",
    color: "#facc15",
    description: "Experienced startup founder and business strategist.",
    actions: ["Startup Validation", "Pitch Deck Review", "Fundraising Tips"],
  },
];

const DEFAULT_MENTOR = mentors[0];

const AIFriend = () => {
  const [selectedMentor, setSelectedMentor] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const chatEndRef = useRef(null);

  /* ---------- Persist Mentor ---------- */
  useEffect(() => {
    const saved = localStorage.getItem("mentor");
    if (saved) setSelectedMentor(JSON.parse(saved));
  }, []);

  useEffect(() => {
    if (selectedMentor) {
      localStorage.setItem("mentor", JSON.stringify(selectedMentor));
    }
  }, [selectedMentor]);

  const activeMentor = selectedMentor || DEFAULT_MENTOR;

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  /* ---------- Send Message ---------- */
  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim() || !selectedMentor) return;

    const userMsg = {
      role: "user",
      content: input,
      time: new Date().toLocaleTimeString([], {
        hour: "2-digit",
        minute: "2-digit",
      }),
    };

    setMessages((prev) => [...prev, userMsg]);
    setInput("");

    try {
      const res = await axios.post("http://localhost:3001/api/mentor/chat", {
        userId: "user123",
        mentorType: selectedMentor.type,
        message: input,
      });
      setMessages(res.data.messages);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="min-h-screen bg-black flex flex-col md:flex-row gap-8 p-8 text-white">

      {/* ================= LEFT ================= */}
      <div className="md:w-1/3 bg-black/50 border border-gray-800 rounded-2xl p-6 text-center">

        <motion.div
          className="w-20 h-20 mx-auto rounded-full flex items-center justify-center text-3xl font-bold text-black"
          style={{ background: activeMentor.color }}
          animate={{ scale: [1, 1.1, 1] }}
        >
          {activeMentor.name.charAt(0)}
        </motion.div>

        <AnimatePresence mode="wait">
          <motion.h2
            key={activeMentor.name}
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            className="mt-4 text-2xl font-semibold"
          >
            {activeMentor.name}
          </motion.h2>
        </AnimatePresence>

        <p className="text-gray-400 text-sm">{activeMentor.type}</p>

        {selectedMentor ? (
          <>
            <p className="text-gray-300 text-sm mt-4">
              {selectedMentor.description}
            </p>

            <button
              onClick={() => setMessages([])}
              className="mt-4 w-full border border-gray-700 rounded-xl py-2 hover:bg-gray-800"
            >
              ðŸ’¬ New Chat
            </button>

            <button
              onClick={() => setSelectedMentor(null)}
              className="mt-3 w-full border border-gray-700 rounded-xl py-2 hover:bg-gray-800"
            >
              ðŸ”„ Change Mentor
            </button>
          </>
        ) : (
          <div className="mt-6 space-y-3">
            {mentors.map((m) => (
              <button
                key={m.id}
                onClick={() => setSelectedMentor(m)}
                className="w-full border border-gray-700 rounded-xl py-3 hover:bg-[#1a1a1d]"
              >
                {m.name} â€” {m.type}
              </button>
            ))}
          </div>
        )}
      </div>

      {/* ================= RIGHT ================= */}
      <div className="md:w-2/3 flex flex-col bg-[#0d0d0f] border border-gray-800 rounded-2xl overflow-hidden">

        {/* Header */}
        <div className="relative p-6 border-b border-gray-800">
          <div
            className="absolute inset-0 blur-2xl opacity-40"
            style={{
              background: `radial-gradient(circle at top left, ${activeMentor.color}33, transparent 60%)`,
            }}
          />
          <div className="relative z-10">
            <h2
              className="text-3xl font-bold bg-clip-text text-transparent"
              style={{
                backgroundImage: `linear-gradient(90deg, ${activeMentor.color}, #a5b4fc)`,
              }}
            >
              {activeMentor.name}
            </h2>
            <p className="text-gray-400 text-sm">{activeMentor.type}</p>

            <div className="mt-4 flex flex-wrap gap-3">
              {activeMentor.actions.map((a) => (
                <button
                  key={a}
                  className="flex items-center gap-2 px-4 py-2 text-sm rounded-xl bg-[#16181c] border border-gray-700"
                >
                  <Zap size={14} style={{ color: activeMentor.color }} />
                  {a}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Chat */}
        <div className="flex-grow overflow-y-auto px-6 py-6 space-y-6">
          {selectedMentor ? (
            messages.length ? (
              messages.map((msg, i) => (
                <div
                  key={i}
                  className={`flex ${msg.role === "user" ? "justify-end" : "justify-start"}`}
                >
                  <div
                    className={`max-w-[70%] px-4 py-3 rounded-2xl text-sm ${
                      msg.role === "user"
                        ? "bg-blue-600 text-white"
                        : "bg-[#1e1e22] border border-gray-800"
                    }`}
                  >
                    {msg.content}
                    <div className="text-[10px] text-gray-400 mt-1 text-right">
                      {msg.time}
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="text-center text-gray-500 mt-20">
                Say hi to {selectedMentor.name} ðŸ‘‹
              </div>
            )
          ) : (
            <div className="text-center text-gray-500 mt-20">
              Select a mentor to start chatting
            </div>
          )}
          <div ref={chatEndRef} />
        </div>

        {/* Input */}
        {selectedMentor && (
          <form
            onSubmit={sendMessage}
            className="flex items-center gap-3 border-t border-gray-800 px-4 py-3"
          >
            <input
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder={`Ask ${selectedMentor.name} anything...`}
              className="flex-grow bg-[#1a1a1d] border border-gray-700 rounded-xl px-4 py-3 text-sm"
            />
            <button
              type="submit"
              className="p-3 rounded-xl text-black"
              style={{ background: activeMentor.color }}
            >
              <Send size={18} />
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default AIFriend;
