// src/components/FeatureCategories.jsx
import React from 'react';
import { motion } from 'framer-motion';
import { FeatureCard } from './FeatureCard'; // Assuming FeatureCard is in the same folder or update path
import { TrophyIcon, FileTextIcon, TargetIcon, UsersIcon } from './Iconcomponents';

// I added one more to show the 4-column layout
const categories = [
  {
    id: "competitions",
    title: "Competition Hub",
    description: "Join exhilarating challenges and showcase your cyber-prowess.",
    icon: TrophyIcon,
    neonColor: "neon-blue",
  },
  {
    id: "resources",
    title: "The Databank",
    description: "Access a curated library of guides, tutorials, and intel.",
    icon: FileTextIcon,
    neonColor: "neon-purple",
  },
  {
    id: "practice",
    title: "Training Grounds",
    description: "Hone your skills in guided simulations and real-world scenarios.",
    icon: TargetIcon,
    neonColor: "neon-green",
  },
  {
    id: "community",
    title: "The Syndicate",
    description: "Connect, collaborate, and form alliances with fellow operatives.",
    icon: UsersIcon,
    neonColor: "neon-pink",
  }
];

// Animation variants for the container
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.2, // This will make cards appear one after another
    },
  },
};

export function FeatureCategories() {
  return (
    <motion.section 
      className="py-20 bg-black relative overflow-hidden"
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.2 }}
      variants={containerVariants}
    >
      <div className="absolute inset-0 cyber-grid opacity-20 animate-grid-pan"></div>
      <div className="absolute inset-0 bg-gradient-to-t from-black via-transparent to-black"></div>

      <div className="container mx-auto px-4 relative z-10">
        <motion.div 
          className="text-center mb-16"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.7, ease: "easeOut" }}
        >
          <h2 className="font-orbitron text-4xl md:text-5xl font-bold mb-6 text-white animate-cyber-flicker">
            Enter The <span className="bg-gradient-to-r from-neon-blue via-neon-purple to-neon-pink bg-clip-text text-transparent">
              Cyber-Verse
            </span>
          </h2>
          <p className="text-xl text-white/80 max-w-3xl mx-auto leading-relaxed">
            From covert ops to the grand stage, discover the arsenal you need to dominate the digital frontier.
          </p>
        </motion.div>

        <motion.div 
          className="grid md:grid-cols-2 lg:grid-cols-4 gap-8"
          variants={containerVariants}
        >
          {categories.map((category) => (
            <FeatureCard key={category.id} {...category} />
          ))}
        </motion.div>
      </div>
    </motion.section>
  );
}
