import React, { useState, useEffect } from 'react';

// --- ICONS ---
// Added new icons for the Trajectory Builder and kept the existing ones.
// Using inline SVGs is a great practice for single-file applications.

const TrophyIcon = ({ className = "h-6 w-6 text-neon-blue" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M6 9H4.5a2.5 2.5 0 0 1 0-5H6"/><path d="M18 9h1.5a2.5 2.5 0 0 0 0-5H18"/><path d="M4 22h16"/><path d="M10 14.66V17c0 .55-.47.98-.97 1.21C7.85 18.75 7 20.24 7 22"/><path d="M14 14.66V17c0 .55.47.98.97 1.21C16.15 18.75 17 20.24 17 22"/><path d="M18 2H6v7a6 6 0 0 0 12 0V2Z"/></svg>
);

const FileTextIcon = ({ className = "h-6 w-6 text-neon-purple" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M14.5 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V7.5L14.5 2z" /><polyline points="14 2 14 8 20 8" /><line x1="16" y1="13" x2="8" y2="13" /><line x1="16" y1="17" x2="8" y2="17" /><line x1="10" y1="9" x2="8" y2="9" /></svg>
);

const TargetIcon = ({ className = "h-6 w-6 text-neon-green" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="12" r="10" /><circle cx="12" cy="12" r="6" /><circle cx="12" cy="12" r="2" /></svg>
);

const UsersIcon = ({ className = "h-6 w-6 text-neon-pink" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M16 21v-2a4 4 0 0 0-4-4H6a4 4 0 0 0-4 4v2" /><circle cx="9" cy="7" r="4" /><path d="M22 21v-2a4 4 0 0 0-3-3.87" /><path d="M16 3.13a4 4 0 0 1 0 7.75" /></svg>
);

const ArrowRight = ({ className = "h-4 w-4" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><line x1="5" y1="12" x2="19" y2="12"></line><polyline points="12 5 19 12 12 19"></polyline></svg>
);

const MapIcon = ({ className = "h-6 w-6 text-neon-yellow" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polygon points="3 6 9 3 15 6 21 3 21 18 15 21 9 18 3 21" /><line x1="9" y1="3" x2="9" y2="18" /><line x1="15" y1="6" x2="15" y2="21" /></svg>
);

const BookOpenIcon = ({ className = "h-5 w-5 mr-2" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><path d="M2 3h6a4 4 0 0 1 4 4v14a3 3 0 0 0-3-3H2z"></path><path d="M22 3h-6a4 4 0 0 0-4 4v14a3 3 0 0 1 3-3h7z"></path></svg>
);
const CodeIcon = ({ className = "h-5 w-5 mr-2" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><polyline points="16 18 22 12 16 6"></polyline><polyline points="8 6 2 12 8 18"></polyline></svg>
);
const AwardIcon = ({ className = "h-5 w-5 mr-2" }) => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}><circle cx="12" cy="8" r="7"></circle><polyline points="8.21 13.89 7 23 12 20 17 23 15.79 13.88"></polyline></svg>
);


// --- MOCK COMPONENTS (Header, Footer, Homepage, ResumeBuilder) ---
// These are placeholders for your actual components to make this file runnable.
const Header = ({ setPage }) => (
    <header className="bg-black/50 backdrop-blur-sm fixed top-0 left-0 right-0 z-50 border-b border-white/10">
        <nav className="container mx-auto px-4 py-4 flex justify-between items-center">
            <h1 className="font-orbitron text-2xl font-bold text-white neon-text cursor-pointer" onClick={() => setPage('home')}>CyberPath</h1>
            <div className="flex items-center space-x-4">
                <a href="#" onClick={() => setPage('home')} className="text-white/80 hover:text-neon-cyan transition">Home</a>
                <a href="#" onClick={() => setPage('opportunities')} className="text-white/80 hover:text-neon-cyan transition">Opportunities</a>
                <a href="#" onClick={() => setPage('resume')} className="text-white/80 hover:text-neon-cyan transition">Resume Builder</a>
            </div>
        </nav>
    </header>
);

const Footer = () => (
    <footer className="bg-black border-t border-white/10 py-8">
        <div className="container mx-auto px-4 text-center text-white/60">
            <p>&copy; {new Date().getFullYear()} CyberPath. All rights reserved.</p>
        </div>
    </footer>
);

const HomePage = () => (
    <div className="bg-black text-white h-screen flex items-center justify-center">
        <div className="text-center">
            <h1 className="text-5xl font-orbitron font-bold">Welcome to CyberPath</h1>
            <p className="text-xl mt-4">Your journey into the digital frontier starts here.</p>
        </div>
    </div>
);

const ResumeBuilder = () => (
    <div className="bg-black text-white py-20 min-h-screen flex items-center justify-center">
        <div className="text-center container mx-auto px-4">
            <h1 className="text-5xl font-orbitron font-bold">AI Resume Builder</h1>
            <p className="text-xl mt-4">Craft the perfect resume for your next cyber role.</p>
        </div>
    </div>
);


// --- THIS IS THE NEW TRAJECTORY BUILDER ---
function TrajectoryBuilder() {
    const [selectedPath, setSelectedPath] = useState(null);
    const [activeStep, setActiveStep] = useState(0);

    const trajectories = {
        ai_ml: {
            title: "AI/ML Engineer",
            color: "neon-cyan",
            steps: [
                {
                    type: 'Learning',
                    title: 'Master Python & Core Libraries',
                    description: 'Build a strong foundation in Python, NumPy, Pandas, and Matplotlib for data manipulation and visualization.',
                    details: ['Codecademy: Python 3', 'FreeCodeCamp: Data Analysis with Python', 'Project: Analyze a public dataset of your choice.']
                },
                {
                    type: 'Learning',
                    title: 'Understand Machine Learning Concepts',
                    description: 'Dive into supervised, unsupervised, and reinforcement learning. Learn about model evaluation and tuning.',
                    details: ['Coursera: Machine Learning by Andrew Ng', 'Book: "Hands-On Machine Learning" by Aurélien Géron']
                },
                {
                    type: 'Project',
                    title: 'Build a Predictive Model',
                    description: 'Create a project that predicts a real-world outcome, such as housing prices or customer churn. Deploy it using Flask.',
                    details: ['Kaggle Datasets are a great resource.', 'Focus on documenting your feature engineering and model selection process.']
                },
                {
                    type: 'Certification',
                    title: 'Specialize in a Deep Learning Framework',
                    description: 'Gain expertise in either TensorFlow or PyTorch. Build and train neural networks for image or text data.',
                    details: ['TensorFlow Developer Certificate', 'PyTorch Challenge on Udacity', 'Project: Image classifier for a custom dataset.']
                },
            ]
        },
        cybersecurity: {
            title: "Cybersecurity Analyst",
            color: "neon-red",
            steps: [
                {
                    type: 'Learning',
                    title: 'Networking & OS Fundamentals',
                    description: 'Understand TCP/IP, DNS, OSI model, and the architecture of Windows and Linux systems.',
                    details: ['CompTIA Network+', 'Professor Messer YouTube series', 'Setup a home lab with VirtualBox.']
                },
                {
                    type: 'Certification',
                    title: 'Earn Foundational Certifications',
                    description: 'Certifications like CompTIA Security+ validate your baseline skills to employers.',
                    details: ['CompTIA Security+ SY0-601', 'Use practice exams from Jason Dion on Udemy.']
                },
                {
                    type: 'Project',
                    title: 'Practice on CTF Platforms',
                    description: 'Engage with platforms like TryHackMe and HackTheBox to apply your skills in practical, hands-on scenarios.',
                    details: ['Complete the "Complete Beginner" learning path on TryHackMe.', 'Document your solutions and thought process.']
                },
                {
                    type: 'Learning',
                    title: 'Learn a Scripting Language',
                    description: 'Python or Bash are essential for automating tasks and analyzing data. Focus on scripting for security tasks.',
                    details: ['Book: "Black Hat Python"', 'Project: Write a simple port scanner or log analyzer.']
                },
            ]
        }
    };

    const getIconForType = (type) => {
        switch (type) {
            case 'Learning': return <BookOpenIcon className="h-5 w-5 mr-3 text-white/70" />;
            case 'Project': return <CodeIcon className="h-5 w-5 mr-3 text-white/70" />;
            case 'Certification': return <AwardIcon className="h-5 w-5 mr-3 text-white/70" />;
            default: return null;
        }
    };

    const currentTrajectory = selectedPath ? trajectories[selectedPath] : null;

    return (
        <section className="py-24 bg-black relative overflow-hidden min-h-screen">
            <div className="absolute inset-0 cyber-grid opacity-20"></div>
            <div className="container mx-auto px-4 relative z-10">
                <div className="text-center mb-16">
                    <h2 className="font-orbitron text-4xl md:text-5xl font-bold mb-6 text-white neon-text">
                        Build Your <span className="bg-gradient-to-r from-neon-yellow via-neon-cyan to-neon-red bg-clip-text text-transparent">
                            Cyber Trajectory
                        </span>
                    </h2>
                    <p className="text-xl text-white/80 max-w-3xl mx-auto leading-relaxed">
                        Select your desired career path and get a curated, step-by-step roadmap to achieve your goals.
                    </p>
                </div>

                {!selectedPath ? (
                    <div className="grid md:grid-cols-2 gap-8 max-w-4xl mx-auto">
                        {Object.keys(trajectories).map(key => (
                            <button key={key} onClick={() => { setSelectedPath(key); setActiveStep(0); }} className={`group p-8 border-0 shadow-xl hologram hover:scale-105 transition-all duration-300 rounded-2xl text-left hover:shadow-2xl hover:shadow-${trajectories[key].color}`}>
                                <h3 className={`font-orbitron text-3xl font-bold text-white mb-3`}>{trajectories[key].title}</h3>
                                <p className="text-white/70 mb-6">A complete roadmap for aspiring {trajectories[key].title}s.</p>
                                <span className={`font-semibold text-${trajectories[key].color} neon-text`}>Start Building <ArrowRight className="inline ml-2 h-4 w-4" /></span>
                            </button>
                        ))}
                    </div>
                ) : (
                    <div className="flex flex-col lg:flex-row gap-10 max-w-6xl mx-auto">
                        <div className="lg:w-1/3">
                            <button onClick={() => setSelectedPath(null)} className="text-white/70 hover:text-white mb-6 transition-colors">
                                &larr; Back to Paths
                            </button>
                            <h3 className="font-orbitron text-3xl font-bold text-white mb-6">
                                Path: <span className={`text-${currentTrajectory.color}`}>{currentTrajectory.title}</span>
                            </h3>
                            <div className="space-y-2">
                                {currentTrajectory.steps.map((step, index) => (
                                    <button key={index} onClick={() => setActiveStep(index)} className={`w-full text-left p-4 rounded-lg border transition-all duration-300 flex items-center ${activeStep === index ? `bg-${currentTrajectory.color}/20 border-${currentTrajectory.color}` : 'bg-black/30 border-white/10 hover:bg-white/5'}`}>
                                        <div className={`mr-4 font-orbitron font-bold text-xl ${activeStep === index ? `text-${currentTrajectory.color}` : 'text-white/50'}`}>
                                            {String(index + 1).padStart(2, '0')}
                                        </div>
                                        <div>
                                           <div className={`font-semibold ${activeStep === index ? 'text-white' : 'text-white/80'}`}>{step.title}</div>
                                           <div className={`text-xs ${activeStep === index ? `text-${currentTrajectory.color}/80` : 'text-white/50'}`}>{step.type}</div>
                                        </div>
                                    </button>
                                ))}
                            </div>
                        </div>
                        <div className="lg:w-2/3">
                           <div className="p-8 rounded-2xl bg-black/30 border border-white/10 backdrop-blur-sm sticky top-28">
                                <div className="mb-6">
                                    <span className={`px-3 py-1 rounded-full text-sm font-semibold bg-${currentTrajectory.color}/20 text-${currentTrajectory.color}`}>
                                        Step {activeStep + 1} of {currentTrajectory.steps.length}
                                    </span>
                                </div>
                                <h4 className="font-orbitron text-2xl font-bold text-white mb-3">{currentTrajectory.steps[activeStep].title}</h4>
                                <p className="text-white/80 leading-relaxed mb-6">{currentTrajectory.steps[activeStep].description}</p>
                                <div className="border-t border-white/10 pt-6">
                                    <h5 className="text-white/70 font-semibold mb-4">Recommended Resources:</h5>
                                    <ul className="space-y-3">
                                        {currentTrajectory.steps[activeStep].details.map((detail, i) => (
                                            <li key={i} className="flex items-start">
                                                {getIconForType(currentTrajectory.steps[activeStep].type)}
                                                <span className="text-white/90">{detail}</span>
                                            </li>
                                        ))}
                                    </ul>
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </section>
    );
}


// --- OPPORTUNITIES PAGE (UPDATED) ---
// I've added the new Trajectory Builder as a feature here for better navigation.
function OpportunitiesPage({ setPage }) {
    const categories = [
      {
        id: "trajectory", // NEW
        title: "Trajectory Builder",
        description: "Visualize your career path with step-by-step guidance.",
        icon: MapIcon,
        neonColor: "neon-yellow",
        count: "2 Paths",
        trending: "New!",
        items: [
          { title: "AI/ML Engineer Path", provider: "CyberPath", type: "Roadmap" },
          { title: "Cybersecurity Analyst Path", provider: "CyberPath", type: "Roadmap" },
          { title: "More paths coming soon!", provider: "", type: "" }
        ]
      },
      {
        id: "competitions",
        title: "Competition Aggregator",
        description: "Join exciting challenges and showcase your skills",
        icon: TrophyIcon,
        neonColor: "neon-blue",
        count: "2,340+",
        trending: "+12 today",
        items: [
          { title: "AI/ML Challenge 2024", company: "Meta", prize: "$10K" },
          { title: "Cyber Wars Hackathon", company: "Google", prize: "$5K" },
          { title: "Blockchain Case Study", company: "Coinbase", prize: "$15K" }
        ]
      },
      {
        id: "resume",
        title: "Resume Builder",
        description: "AI-powered tools to create the perfect resume",
        icon: FileTextIcon,
        neonColor: "neon-purple",
        count: "1,890+",
        trending: "+7 today",
        items: [
          { title: "AI Resume Builder", provider: "PathFinder", type: "Tool" },
          { title: "Mock Interviews", provider: "CyberPrep", type: "Practice" },
          { title: "Portfolio Review", provider: "DigitalCrit", type: "Feedback" }
        ]
      },
      {
        id: "community",
        title: "Community Platform",
        description: "Connect, collaborate, and grow with peers",
        icon: UsersIcon,
        neonColor: "neon-pink",
        count: "3,420+",
        trending: "+18 today",
        items: [
          { title: "Study Groups", commitment: "2h/week" },
          { title: "Mentorship Program", commitment: "1h/week" },
          { title: "Skill Exchange", commitment: "Flexible" }
        ]
      }
    ];

    return (
        <section className="py-24 bg-black relative overflow-hidden">
            <div className="absolute inset-0 cyber-grid opacity-20"></div>
            <div className="container mx-auto px-4 relative z-10">
                <div className="text-center mb-16">
                    <h2 className="font-orbitron text-4xl md:text-5xl font-bold mb-6 text-white neon-text">
                        Explore Our <span className="bg-gradient-to-r from-neon-blue via-neon-purple to-neon-pink bg-clip-text text-transparent">
                            Cyber Features
                        </span>
                    </h2>
                    <p className="text-xl text-white/80 max-w-3xl mx-auto leading-relaxed">
                        From competitions to skill building, discover all the digital tools you need to accelerate your career journey.
                    </p>
                </div>
                <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
                    {categories.map((category) => {
                        const Icon = category.icon;
                        return (
                            <div key={category.id} className={`group flex flex-col hover:shadow-2xl transition-all duration-500 border-0 shadow-xl hologram hover:scale-105 hover:-translate-y-2 rounded-2xl overflow-hidden hover:shadow-${category.neonColor}`}>
                                <div className={`h-2 bg-gradient-to-r from-${category.neonColor} to-transparent`}></div>
                                <div className="p-6 flex-grow flex flex-col">
                                    <div className="flex items-start justify-between mb-4">
                                        <div className={`p-3 inline-block rounded-xl bg-${category.neonColor}/20 border border-${category.neonColor}/30 shadow-lg transform group-hover:scale-110 transition-all duration-300`}>
                                            <Icon className={`h-8 w-8 text-${category.neonColor}`} />
                                        </div>
                                        <div className="text-right">
                                            <div className={`text-lg font-bold text-${category.neonColor} neon-text`}>{category.count}</div>
                                            <div className={`px-2 py-0.5 text-xs rounded-full ${category.trending === 'New!' ? 'bg-gradient-to-r from-neon-yellow to-neon-orange text-black' : 'bg-gradient-to-r from-neon-green to-neon-cyan text-black'} border-0 font-semibold`}>
                                                {category.trending}
                                            </div>
                                        </div>
                                    </div>
                                    <h3 className="font-orbitron text-xl font-bold text-white mb-2">{category.title}</h3>
                                    <p className="text-white/80 leading-relaxed mb-6">{category.description}</p>
                                    <div className="space-y-3 mt-auto pt-6 border-t border-white/10">
                                        {category.items.map((item, index) => (
                                            <div key={index} className="bg-black/30 backdrop-blur-sm rounded-lg p-3 border border-white/10 text-xs">
                                                <div className="font-semibold text-white mb-1 truncate">{item.title}</div>
                                                <div className="flex items-center justify-between text-white/70">
                                                    <span>{item.company || item.provider}</span>
                                                    <span className={`font-bold text-${category.neonColor}`}>{item.prize || item.spots || item.type || item.commitment}</span>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                                <div className="p-6 pt-0 mt-4">
                                     {/* This button now handles navigation */}
                                    <button 
                                        onClick={() => category.id === 'trajectory' ? setPage('trajectory') : category.id === 'resume' ? setPage('resume') : null}
                                        className={`w-full bg-gradient-to-r from-${category.neonColor} to-${category.neonColor}/80 hover:shadow-xl text-white border-0 rounded-xl py-3 font-semibold transform hover:scale-105 transition-all duration-300 neon-text disabled:opacity-50 disabled:cursor-not-allowed`}
                                        disabled={!['trajectory', 'resume'].includes(category.id)}
                                    >
                                        Explore Feature <ArrowRight className="inline ml-2 h-4 w-4" />
                                    </button>
                                </div>
                            </div>
                        );
                    })}
                </div>
            </div>
        </section>
    );
}


// --- MAIN APP COMPONENT (UPDATED) ---
function App() {
    const [page, setPage] = useState('home');

    useEffect(() => {
        // Add a global stylesheet for the cyberpunk theme
        const style = document.createElement('style');
        style.innerHTML = `
            @import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400;700&family=Roboto:wght@400;500&display=swap');

            body {
                font-family: 'Roboto', sans-serif;
                background-color: #000;
            }
            .font-orbitron { font-family: 'Orbitron', sans-serif; }

            .cyber-grid {
                background-image:
                    linear-gradient(to right, rgba(0, 255, 255, 0.1) 1px, transparent 1px),
                    linear-gradient(to bottom, rgba(0, 255, 255, 0.1) 1px, transparent 1px);
                background-size: 40px 40px;
            }

            .hologram {
                background: rgba(0,0,0,0.4);
                border: 1px solid rgba(255, 255, 255, 0.1);
                backdrop-filter: blur(10px);
                -webkit-backdrop-filter: blur(10px);
            }

            .neon-text {
                text-shadow:
                    0 0 5px rgba(255, 255, 255, 0.8),
                    0 0 10px rgba(0, 255, 255, 0.6),
                    0 0 20px rgba(0, 255, 255, 0.4),
                    0 0 40px rgba(0, 255, 255, 0.2);
            }
            .neon-blue { color: #00ffff; }
            .text-neon-blue { color: #00ffff; }
            .hover\:shadow-neon-blue:hover { box-shadow: 0 0 25px rgba(0, 255, 255, 0.6); }
            .bg-neon-blue\\/20 { background-color: rgba(0, 255, 255, 0.2); }
            .border-neon-blue\\/30 { border-color: rgba(0, 255, 255, 0.3); }
            .from-neon-blue { --tw-gradient-from: #00ffff; --tw-gradient-stops: var(--tw-gradient-from), var(--tw-gradient-to, rgba(0, 255, 255, 0)); }

            .neon-purple { color: #be00ff; }
            .text-neon-purple { color: #be00ff; }
            .hover\:shadow-neon-purple:hover { box-shadow: 0 0 25px rgba(190, 0, 255, 0.6); }
            .bg-neon-purple\\/20 { background-color: rgba(190, 0, 255, 0.2); }
