// src/pages/AIFriend.js
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import { Send, X, Sparkles, MessageCircle } from "lucide-react";

const mentors = [
  { type: "Tech Mentor", name: "Fazal", color: "#4ade80", description: "Expert in software development, AI, and cloud technologies." },
  { type: "Design Mentor", name: "Alaana", color: "#f472b6", description: "Creative designer specializing in UX/UI and digital art." },
  { type: "Career Coach", name: "Khaliq and Shafiq", color: "#60a5fa", description: "Helping professionals identify strengths and plan their careers." },
  { type: "Entrepreneur Guide", name: "Omar", color: "#facc15", description: "Experienced startup founder and business strategist." },
  { type: "Public Speaking Coach", name: "Buffy", color: "#a78bfa", description: "Coach in communication, leadership, and presentation skills." },
  { type: "Dance Coach", name: "Dheeya", color: "#f87171", description: "Professional dancer guiding you through choreography and expression." },
  { type: "Sports Coach", name: "Samyak and Naman", color: "#34d399", description: "Trains in fitness, agility, and sports strategy." },
  { type: "Music Mentor", name: "Soham", color: "#60a5fa", description: "Expert in music theory, instruments, and vocal coaching." },
  { type: "Photography Mentor", name: "APG", color: "#f59e0b", description: "Drone photography and creative visual storytelling expert." },
];

const AIFriend = () => {
  const [selectedMentor, setSelectedMentor] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim() || !selectedMentor) return;
    const newMsg = { role: "user", content: input };
    setMessages((prev) => [...prev, newMsg]);
    setInput("");
    try {
      const res = await axios.post("http://localhost:3001/api/mentor/chat", {
        userId: "user123",
        mentorType: selectedMentor.type,
        message: input,
      });
      setMessages(res.data.messages);
    } catch (err) {
      console.error("Error sending message:", err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-black via-slate-950 to-black text-white font-orbitron flex flex-col md:flex-row items-center md:items-start justify-center p-8 space-y-8 md:space-y-0 md:space-x-8">

      {/* Left Panel - Mentor Info */}
      <motion.div
        initial={{ x: -20, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.6 }}
        className="w-full md:w-1/3 bg-black/50 border border-neon-purple/40 rounded-2xl p-6 backdrop-blur-xl shadow-lg flex flex-col items-center text-center"
      >
        <div className="flex flex-col items-center mb-4">
          <div className="w-20 h-20 rounded-full flex items-center justify-center text-3xl font-bold text-black shadow-lg"
            style={{
              background: selectedMentor ? selectedMentor.color : "#60a5fa",
              boxShadow: `0 0 25px ${selectedMentor ? selectedMentor.color : "#60a5fa"}70`,
            }}>
            {selectedMentor ? selectedMentor.name.charAt(0) : "A"}
          </div>
          <h2 className="mt-4 text-2xl font-bold text-neon-purple">
            {selectedMentor ? selectedMentor.name : "Your AI Friend"}
          </h2>
          <p className="text-sm text-neon-blue mt-1">
            {selectedMentor ? selectedMentor.type : "Choose a mentor to begin"}
          </p>
        </div>

        {selectedMentor ? (
          <>
            <p className="text-gray-300 text-sm mb-6">{selectedMentor.description}</p>
            <div className="flex flex-col w-full space-y-3">
              <button onClick={() => setMessages([])} className="bg-black/40 border border-neon-blue/40 px-4 py-2 rounded-xl hover:bg-neon-blue/20 transition">ðŸ’¬ New Chat</button>
              <button onClick={() => setSelectedMentor(null)} className="bg-black/40 border border-neon-purple/40 px-4 py-2 rounded-xl hover:bg-neon-purple/20 transition">ðŸ”„ Change Mentor</button>
            </div>
          </>
        ) : (
          <div className="w-full space-y-4">
            <p className="text-gray-400 text-sm mb-2">Select a mentor to start chatting:</p>
            {mentors.slice(0, 4).map((m) => (
              <button key={m.name}
                onClick={() => setSelectedMentor(m)}
                className="w-full bg-black/40 border border-neon-blue/40 px-4 py-3 rounded-xl hover:bg-neon-blue/20 transition text-sm">
                {m.name} â€” {m.type}
              </button>
            ))}
          </div>
        )}
      </motion.div>

      {/* Right Panel - Chat */}
      <motion.div
        initial={{ x: 20, opacity: 0 }}
        animate={{ x: 0, opacity: 1 }}
        transition={{ duration: 0.6 }}
        className="w-full md:w-2/3 flex flex-col bg-black/60 border border-neon-blue/40 rounded-2xl p-6 shadow-lg backdrop-blur-xl"
      >
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-semibold text-neon-purple flex items-center space-x-2">
            <Sparkles className="w-5 h-5 text-neon-blue" />
            <span>{selectedMentor ? `${selectedMentor.name} (${selectedMentor.type})` : "Welcome"}</span>
          </h3>
          {selectedMentor && (
            <button onClick={() => setSelectedMentor(null)} className="text-gray-400 hover:text-red-400 transition">
              <X className="w-5 h-5" />
            </button>
          )}
        </div>

        {/* Chat Messages */}
        <div className="flex-grow overflow-y-auto space-y-4 mb-4 p-2">
          {selectedMentor ? (
            messages.map((msg, i) => (
              <motion.div key={i} initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.3 }}
                className={`flex ${msg.role === "user" ? "justify-end" : "justify-start"}`}>
                <div className={`px-4 py-3 rounded-2xl max-w-[75%] text-sm ${
                  msg.role === "user"
                    ? "bg-gradient-to-r from-neon-blue to-neon-purple text-black"
                    : "bg-purple-900/70 text-white border border-neon-purple/30"
                }`}>
                  {msg.content}
                </div>
              </motion.div>
            ))
          ) : (
            <div className="text-gray-400 text-center mt-10 text-sm">
              <MessageCircle className="mx-auto mb-2 text-neon-blue" />
              Select a mentor from the left to start chatting!
            </div>
          )}
          <div ref={chatEndRef} />
        </div>

        {/* Chat Input */}
        {selectedMentor && (
          <form onSubmit={sendMessage} className="flex items-center space-x-3">
            <input
              type="text"
              placeholder={`Ask ${selectedMentor.name} something...`}
              className="flex-grow bg-black/50 border border-neon-purple/40 text-white px-4 py-3 rounded-2xl focus:outline-none focus:ring-2 focus:ring-neon-blue"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
            <button
              type="submit"
              className="bg-gradient-to-r from-neon-purple to-neon-blue hover:opacity-90 text-white px-5 py-3 rounded-2xl font-semibold flex items-center space-x-2 transition-all duration-300"
            >
              <Send className="w-5 h-5" />
              <span>Send</span>
            </button>
          </form>
        )}
      </motion.div>
    </div>
  );
};

export default AIFriend;
