// src/App.js - A Proper React Frontend Component

import React, { useState, useEffect } from 'react';
import './index.css'; // You can keep your CSS import

// This is the correct way for the frontend (React) to be structured.
// Notice there are NO imports for 'express' or 'cors'.
function App() {
  // 'useState' is used to store data inside a component
  const [opportunities, setOpportunities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 'useEffect' runs code after the component first renders
  useEffect(() => {
    // This is the "Waiter" making a request to the "Kitchen" (your server.js)
    // We are fetching data from the API you built.
    fetch('http://localhost:3001/api/opportunities')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setOpportunities(data); // Save the fetched data
        setLoading(false); // We are done loading
      })
      .catch(error => {
        setError(error.message); // If something went wrong, save the error message
        setLoading(false);
      });
  }, []); // The empty array [] means this effect runs only once

  // --- Displaying the content ---

  if (loading) {
    return <div>Loading opportunities... Please make sure your server is running in the other terminal!</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="App">
      <header>
        <h1>Welcome to PathFinder</h1>
      </header>
      <main>
        <h2>Latest Opportunities</h2>
        <div className="opportunities-list">
          {opportunities.map(op => (
            <div className="opportunity-card" key={op.id}>
              <h3>{op.title} <span className="opportunity-type">({op.type})</span></h3>
              <p>{op.description}</p>
              <a href={op.url} target="_blank" rel="noopener noreferrer">Learn More</a>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
