// src/pages/AIFriend.js
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import { Send } from "lucide-react";

// Mentor data
const mentors = [
  {
    type: "Tech Mentor",
    name: "Fazal",
    color: "#4ade80",
    description: "Expert in software development, AI, and cloud technologies.",
  },
  {
    type: "Design Mentor",
    name: "Alaana",
    color: "#f472b6",
    description: "Creative designer specializing in UX/UI and digital art.",
  },
  {
    type: "Career Coach",
    name: "Khaliq and Shafiq",
    color: "#60a5fa",
    description:
      "Helping professionals identify strengths and plan their careers.",
  },
  {
    type: "Entrepreneur Guide",
    name: "Omar",
    color: "#facc15",
    description: "Experienced startup founder and business strategist.",
  },
  {
    type: "Public Speaking Coach",
    name: "Buffy",
    color: "#a78bfa",
    description:
      "Coach in communication, leadership, and presentation skills.",
  },
  {
    type: "Dance Coach",
    name: "Dheeya",
    color: "#f87171",
    description:
      "Professional dancer guiding you through choreography and expression.",
  },
  {
    type: "Sports Coach",
    name: "Samyak and Naman",
    color: "#34d399",
    description: "Trains in fitness, agility, and sports strategy.",
  },
  {
    type: "Music Mentor",
    name: "Soham",
    color: "#60a5fa",
    description: "Expert in music theory, instruments, and vocal coaching.",
  },
  {
    type: "Photography Mentor",
    name: "APG",
    color: "#f59e0b",
    description: "Drone photography and creative visual storytelling expert.",
  },
];

const AIFriend = () => {
  const [selectedMentor, setSelectedMentor] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim() || !selectedMentor) return;

    const newMsg = { role: "user", content: input };
    setMessages((prev) => [...prev, newMsg]);
    setInput("");

    try {
      const res = await axios.post("http://localhost:3001/api/mentor/chat", {
        userId: "user123",
        mentorType: selectedMentor.type,
        message: input,
      });
      setMessages(res.data.messages);
    } catch (err) {
      console.error("Error sending message:", err);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-black via-purple-950 to-black text-white font-orbitron flex flex-col items-center py-10 px-4">
      <h1 className="text-5xl font-bold mb-10 text-transparent bg-clip-text bg-gradient-to-r from-neon-blue to-neon-purple drop-shadow-lg">
        ðŸ¤– Meet Your AI Mentors
      </h1>

      {/* Mentor Selection */}
      {!selectedMentor && (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8 w-full max-w-6xl">
          {mentors.map((mentor, index) => (
            <motion.div
              key={mentor.type}
              whileHover={{ scale: 1.05, y: -5 }}
              transition={{ type: "spring", stiffness: 200 }}
              onClick={() => setSelectedMentor(mentor)}
              className="cursor-pointer bg-black/50 backdrop-blur-xl border border-neon-blue/40 rounded-2xl p-6 hover:shadow-[0_0_15px_var(--tw-shadow-color)] hover:shadow-neon-purple transition-all"
            >
              <div
                className="w-24 h-24 mx-auto rounded-full flex items-center justify-center text-3xl font-bold text-black mb-4 shadow-lg"
                style={{
                  background: `linear-gradient(135deg, ${mentor.color}, #fff)`,
                  boxShadow: `0 0 25px ${mentor.color}80`,
                }}
              >
                {mentor.name.charAt(0)}
              </div>
              <h2 className="text-xl font-semibold text-center mb-1">
                {mentor.name}
              </h2>
              <p className="text-sm text-center text-neon-blue mb-2">
                {mentor.type}
              </p>
              <p className="text-xs text-center text-gray-300 mb-4">
                {mentor.description}
              </p>
              <p className="text-sm text-center text-neon-purple font-medium">
                ðŸ’¬ Chat to explore your career path
              </p>
            </motion.div>
          ))}
        </div>
      )}

      {/* Chat Window */}
      {selectedMentor && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="w-full max-w-3xl flex flex-col items-center"
        >
          <div className="mb-6 flex items-center justify-between w-full bg-black/60 backdrop-blur-xl border border-neon-purple/40 rounded-2xl p-4 shadow-lg">
            <div className="flex items-center space-x-3">
              <div
                className="w-14 h-14 rounded-full flex items-center justify-center text-lg font-bold text-black"
                style={{
                  backgroundColor: selectedMentor.color,
                  boxShadow: `0 0 25px ${selectedMentor.color}70`,
                }}
              >
                {selectedMentor.name.charAt(0)}
              </div>
              <div>
                <h2 className="text-xl font-bold text-neon-purple">
                  {selectedMentor.name}
                </h2>
                <p className="text-sm text-neon-blue">{selectedMentor.type}</p>
              </div>
            </div>
            <button
              className="text-sm text-gray-300 hover:text-neon-blue transition"
              onClick={() => {
                setSelectedMentor(null);
                setMessages([]);
              }}
            >
              ðŸ”„ Change Mentor
            </button>
          </div>

          {/* Chat box */}
          <div className="w-full bg-black/60 border border-neon-blue/40 rounded-2xl p-6 shadow-inner overflow-y-auto h-[60vh] mb-4 backdrop-blur-xl">
            {messages.map((msg, i) => (
              <motion.div
                key={i}
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3 }}
                className={`my-3 flex ${
                  msg.role === "user" ? "justify-end" : "justify-start"
                }`}
              >
                <div
                  className={`max-w-[75%] px-4 py-3 rounded-2xl text-sm ${
                    msg.role === "user"
                      ? "bg-gradient-to-r from-neon-blue to-neon-purple text-black"
                      : "bg-gradient-to-r from-purple-800 to-neon-purple text-white"
                  } shadow-md`}
                >
                  {msg.content}
                </div>
              </motion.div>
            ))}
            <div ref={chatEndRef} />
          </div>

          {/* Input box */}
          <form
            onSubmit={sendMessage}
            className="w-full flex items-center space-x-3"
          >
            <input
              type="text"
              placeholder={`Ask ${selectedMentor.name} something...`}
              className="flex-grow bg-black/50 border border-neon-purple/40 text-white px-4 py-3 rounded-2xl focus:outline-none focus:ring-2 focus:ring-neon-blue backdrop-blur-xl"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
            <button
              type="submit"
              className="bg-gradient-to-r from-neon-purple to-neon-blue hover:opacity-90 text-white px-5 py-3 rounded-2xl font-semibold flex items-center space-x-2 transition-all duration-300"
            >
              <Send className="w-5 h-5" />
              <span>Send</span>
            </button>
          </form>
        </motion.div>
      )}
    </div>
  );
};

export default AIFriend;
