import React, { useState, useEffect } from 'react';
import './index.css';
import ResumeBuilder from './Resumebuilder'; // We still need this

// --- Helper Components for the new UI ---

// A glowing, futuristic header
const Header = ({ page, setPage }) => (
    <header className="relative z-10 flex items-center justify-between p-4 md:p-6 text-white border-b border-cyan-400/20">
        <div className="font-orbitron text-2xl md:text-3xl font-bold tracking-widest text-neon-blue neon-text">
            PathFinder
        </div>
        <nav className="flex items-center gap-4">
            <button 
                onClick={() => setPage('opportunities')} 
                className={`px-4 py-2 rounded-md transition-all duration-300 font-semibold text-sm md:text-base ${page === 'opportunities' ? 'bg-neon-blue text-black shadow-neon-blue' : 'bg-white/10 hover:bg-white/20'}`}>
                Opportunities
            </button>
            <button 
                onClick={() => setPage('resume')} 
                className={`px-4 py-2 rounded-md transition-all duration-300 font-semibold text-sm md:text-base ${page === 'resume' ? 'bg-neon-blue text-black shadow-neon-blue' : 'bg-white/10 hover:bg-white/20'}`}>
                AI Resume Builder
            </button>
        </nav>
    </header>
);

// The new "holographic" category selection view
const OpportunitiesView = ({ onSelectCategory }) => {
    const categories = [
        { name: "Internship", icon: "üíº", description: "Gain practical experience.", color: "cyan" },
        { name: "Competition", icon: "üèÜ", description: "Battle for excellence.", color: "purple" },
        { name: "Webinar", icon: "üíª", description: "Learn from the experts.", color: "pink" },
        { name: "Online Course", icon: "üìö", description: "Sharpen your skills.", color: "cyan" },
        { name: "Community Service", icon: "‚ù§Ô∏è", description: "Make a real impact.", color: "purple" },
    ];

    const colorClasses = {
        cyan: 'border-cyan-400/50 hover:shadow-cyan-400/50',
        purple: 'border-purple-400/50 hover:shadow-purple-400/50',
        pink: 'border-pink-400/50 hover:shadow-pink-400/50',
    };

    return (
        <div className="text-center p-4 md:p-8">
            <h1 className="font-orbitron text-4xl md:text-6xl font-bold text-white mb-4 neon-text">UNLOCK YOUR FUTURE</h1>
            <p className="text-lg md:text-xl text-white/80 max-w-3xl mx-auto mb-12">
                Discover a universe of opportunities. Compete, learn, and build the career of your dreams.
            </p>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 md:gap-8">
                {categories.map(category => (
                    <div 
                        key={category.name} 
                        className={`p-6 bg-black/50 border ${colorClasses[category.color]} backdrop-blur-sm rounded-xl cursor-pointer transition-all duration-300 hover:-translate-y-2 group`}
                        onClick={() => onSelectCategory(category.name)}
                    >
                        <div className="text-4xl mb-4 transform transition-transform duration-300 group-hover:scale-110">{category.icon}</div>
                        <h3 className="font-orbitron text-2xl font-bold text-white mb-2">{category.name}</h3>
                        <p className="text-white/70 transition-colors duration-300 group-hover:text-white/90">{category.description}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

// The view for the list of opportunities, now also styled with Tailwind
const OpportunityListView = ({ category, onBack, opportunities }) => {
    const filteredOps = opportunities.filter(op => op.type === category);

    return (
        <div className="p-4 md:p-8">
            <button onClick={onBack} className="mb-8 px-4 py-2 rounded-md transition-all duration-300 font-semibold bg-white/10 hover:bg-white/20 text-white">&larr; Back to Categories</button>
            <h2 className="font-orbitron text-3xl font-bold text-white text-center mb-8">{category} Opportunities</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {filteredOps.length > 0 ? (
                    filteredOps.map(op => (
                        <div key={op.id} className="bg-black/50 border border-purple-400/30 backdrop-blur-sm rounded-lg p-6 flex flex-col">
                            <h3 className="text-xl font-bold text-neon-purple mb-2">{op.title}</h3>
                            <p className="text-white/80 flex-grow mb-4">{op.description}</p>
                            <a href={op.url} target="_blank" rel="noopener noreferrer" className="mt-auto text-center font-semibold bg-purple-600/50 hover:bg-purple-600 text-white py-2 px-4 rounded-md transition-colors duration-300">
                                Learn More
                            </a>
                        </div>
                    ))
                ) : (
                    <p className="col-span-full text-center text-white/70">No opportunities found for this category right now.</p>
                )}
            </div>
        </div>
    );
};

// Main App Component - updated to use the new components
function App() {
    const [page, setPage] = useState('opportunities');
    const [opportunities, setOpportunities] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState(null);

    useEffect(() => {
        // NOTE: This fetch URL is correct for your development setup (running two terminals)
        fetch('http://localhost:3001/api/opportunities')
            .then(response => {
                if (!response.ok) throw new Error('Load failed');
                return response.json();
            })
            .then(data => {
                setOpportunities(data);
                setLoading(false);
            })
            .catch(error => {
                setError(error.message);
                setLoading(false);
            });
    }, []);

    const renderOpportunities = () => {
        if (loading) return <p className="text-center text-white/70 text-lg p-8">Loading opportunities...</p>;
        if (error) return <p className="text-center text-red-400 text-lg p-8">Error: {error}. Is your server running?</p>;

        return selectedCategory ? (
            <OpportunityListView
                category={selectedCategory}
                onBack={() => setSelectedCategory(null)}
                opportunities={opportunities}
            />
        ) : (
            <OpportunitiesView onSelectCategory={setSelectedCategory} />
        );
    };

    return (
        <div className="min-h-screen">
            <Header page={page} setPage={setPage} />
            <main className="container mx-auto">
                {page === 'opportunities' ? renderOpportunities() : <ResumeBuilder />}
            </main>
        </div>
    );
}

export default App;
