// src/App.js - The Complete Frontend Component for PathFinder

import React, { useState, useEffect } from 'react';
import './index.css'; // This imports the new styles you added

// This is the correct way for the frontend (React) to be structured.
// Notice there are NO imports for 'express' or 'cors'.
function App() {
  // 'useState' is used to store data inside the component
  const [opportunities, setOpportunities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 'useEffect' runs code once after the component first renders
  useEffect(() => {
    // This is the "Waiter" making a request to the "Kitchen" (your server.js)
    // We are fetching the list of opportunities from the API you built.
    fetch('http://localhost:3001/api/opportunities')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok. Is the server running?');
        }
        return response.json();
      })
      .then(data => {
        setOpportunities(data); // Save the fetched data into our state
        setLoading(false);      // We are done loading
      })
      .catch(error => {
        setError(error.message); // If something went wrong, save the error message
        setLoading(false);
      });
  }, []); // The empty array [] means this effect runs only one time

  // --- Displaying the content ---

  // Show a loading message while fetching data
  if (loading) {
    return <div style={{ padding: '2rem' }}>Loading opportunities... Please make sure your server is running in the other terminal!</div>;
  }

  // Show an error message if the fetch failed
  if (error) {
    return <div style={{ padding: '2rem', color: 'red' }}>Error: {error}</div>;
  }

  // If loading is done and there are no errors, show the main page
  return (
    <div className="App">
      <header>
        <h1>PathFinder</h1>
        {/* In the future, you can add a navigation bar here */}
      </header>

      <main>
        <h2>Latest Opportunities</h2>
        <div className="opportunities-list">
          {opportunities.map(op => {
            // This logic creates a simple class name like "card-competition" or "card-internship"
            // to give each card a different color, just like in the example design.
            const typeClass = op.type.split(' ')[0].toLowerCase();
            const cardClassName = `opportunity-card card-${typeClass}`;

            return (
              <div className={cardClassName} key={op.id}>
                <h3>
                  {op.title} 
                  <span className="opportunity-type">({op.type})</span>
                </h3>
                <p>{op.description}</p>
                <a href={op.url} target="_blank" rel="noopener noreferrer">Learn More</a>
              </div>
            );
          })}
        </div>
      </main>
    </div>
  );
}

export default App;
