    analysis.weakPhrases = [...new Set(foundWeakPhrases)]; // Get unique phrases
    suggestions.push(`Replace weak phrases like "${analysis.weakPhrases.join(', ')}" with strong action verbs (e.g., 'Engineered', 'Managed', 'Developed').`);
  }
  if (!/\d/.test(text)) {
    // Checks if there are any numbers in the text
