    skills: `Technical Skills: ${analysis.foundSkills.join(', ')}\nSoft Skills: Leadership, Communication, Problem-Solving`
  };

  // --- Recommendation Phase (The Integration!) ---
  let recommendedOpportunities = [];
  let requiredKeywords = prefs.dreamJob.toLowerCase().split(' ');

  // Add keywords for missing skills
  if (prefs.dreamJob.toLowerCase().includes('software') && !analysis.foundSkills.includes('javascript')) {
    requiredKeywords.push('javascript');
  }
  opportunities.forEach(op => {
    // If an opportunity's keywords match any of the required keywords for the dream job
    if (op.keywords.some(kw => requiredKeywords.includes(kw))) {
      recommendedOpportunities.push(op);
    }
  });
  return {
    analysis,
    suggestions,
    improvedResume,
    recommendedOpportunities: recommendedOpportunities.slice(0, 2) // Return the top 2 recommendations
  };
}

// --- 6. START THE SERVER ---
app.listen(PORT, () => {
  console.log(`✅ PathFinder server is running on http://localhost:${PORT}`);
  console.log('This is the "Kitchen". Now go start your React app (the "Dining Room") in another terminal!');
});

