// src/pages/AIFriend.js
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { motion } from "framer-motion";
import { Send } from "lucide-react";

const mentors = [
  { type: "Tech Mentor", name: "Fazal", description: "Expert in software development, AI, and cloud technologies." },
  { type: "Design Mentor", name: "Alaana", description: "Creative designer specializing in UX/UI and digital art." },
  { type: "Career Coach", name: "Khaliq and Shafiq", description: "Helping professionals identify strengths and plan their careers." },
  { type: "Entrepreneur Guide", name: "Omar", description: "Experienced startup founder and business strategist." },
  { type: "Public Speaking Coach", name: "Buffy", description: "Coach in communication, leadership, and presentation skills." },
  { type: "Dance Coach", name: "Dheeya", description: "Professional dancer guiding you through choreography and expression." },
  { type: "Sports Coach", name: "Samyak and Naman", description: "Trains in fitness, agility, and sports strategy." },
  { type: "Music Mentor", name: "Soham", description: "Expert in music theory, instruments, and vocal coaching." },
  { type: "Photography Mentor", name: "APG", description: "Drone photography and creative visual storytelling expert." },
];

const AIFriend = () => {
  const [selectedMentor, setSelectedMentor] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim() || !selectedMentor) return;
    const newMsg = { role: "user", content: input };
    setMessages((prev) => [...prev, newMsg]);
    setInput("");
    try {
      const res = await axios.post("http://localhost:3001/api/mentor/chat", {
        userId: "user123",
        mentorType: selectedMentor.type,
        message: input,
      });
      setMessages(res.data.messages);
    } catch (err) {
      console.error("Error sending message:", err);
    }
  };

  // Auto greeting when mentor is selected
  useEffect(() => {
    if (selectedMentor) {
      setMessages([
        {
          role: "assistant",
          content: `Hi! I'm ${selectedMentor.name}, your ${selectedMentor.type}. ${selectedMentor.description} How can I help you today?`,
        },
      ]);
    }
  }, [selectedMentor]);

  return (
    <div className="min-h-screen w-full bg-[#0a0a0a] flex items-center justify-center text-white font-sans px-4 py-8">
      {!selectedMentor ? (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.4 }}
          className="w-full max-w-md bg-[#111] border border-gray-800 rounded-2xl p-6 shadow-2xl"
        >
          <h2 className="text-2xl font-semibold mb-6 text-center">Choose Your Mentor</h2>
          <div className="space-y-3">
            {mentors.map((m) => (
              <button
                key={m.name}
                onClick={() => setSelectedMentor(m)}
                className="w-full text-left bg-[#1a1a1a] hover:bg-[#222] px-4 py-3 rounded-xl border border-gray-700 transition-all"
              >
                <p className="font-medium">{m.name}</p>
                <p className="text-sm text-gray-400">{m.type}</p>
              </button>
            ))}
          </div>
        </motion.div>
      ) : (
        <motion.div
          initial={{ opacity: 0, y: 15 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.4 }}
          className="w-full max-w-2xl bg-[#111] border border-gray-800 rounded-2xl shadow-2xl flex flex-col overflow-hidden"
        >
          {/* Header */}
          <div className="p-5 border-b border-gray-800 flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center text-lg font-bold">
                {selectedMentor.name.charAt(0)}
              </div>
              <div>
                <p className="font-semibold">{selectedMentor.name}</p>
                <p className="text-xs text-gray-400">{selectedMentor.type}</p>
              </div>
            </div>
            <button
              onClick={() => setSelectedMentor(null)}
              className="text-gray-400 hover:text-red-400 text-sm transition"
            >
              End Chat âœ•
            </button>
          </div>

          {/* Messages */}
          <div className="flex-grow overflow-y-auto p-5 space-y-4 scrollbar-thin scrollbar-thumb-gray-700">
            {messages.map((msg, i) => (
              <motion.div
                key={i}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
                className={`flex ${msg.role === "user" ? "justify-end" : "justify-start"}`}
              >
                <div
                  className={`max-w-[75%] px-4 py-3 rounded-2xl text-sm ${
                    msg.role === "user"
                      ? "bg-blue-600 text-white"
                      : "bg-[#1c1c1c] border border-gray-700 text-gray-100"
                  }`}
                >
                  {msg.content}
                  <div className="text-[10px] text-gray-400 mt-1 text-right">
                    {new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })}
                  </div>
                </div>
              </motion.div>
            ))}
            <div ref={chatEndRef} />
          </div>

          {/* Input */}
          <form onSubmit={sendMessage} className="p-4 border-t border-gray-800 flex items-center space-x-3">
            <input
              type="text"
              placeholder={`Ask ${selectedMentor.name} anything...`}
              className="flex-grow bg-[#1a1a1a] text-white px-4 py-3 rounded-xl border border-gray-700 focus:outline-none focus:ring-1 focus:ring-gray-600 text-sm"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
            <button
              type="submit"
              className="bg-blue-600 hover:bg-blue-700 px-4 py-3 rounded-xl text-white flex items-center justify-center transition"
            >
              <Send className="w-4 h-4" />
            </button>
          </form>
        </motion.div>
      )}
    </div>
  );
};

export default AIFriend;
